import React, { useState } from "react";
import { IoMdClose } from "react-icons/io";
import { authAxios } from "../../utils";
import { addAddressURL } from "../../constant";
import "./style.css";

const AddAddress = () => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [state, setState] = useState("Nairobi");
  const [city, setCity] = useState("Nairobi");
  const [defaultAddress, setDefaultAddress] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = {
      firstName,
      lastName,
      phone,
      address,
      state,
      city,
      defaultAddress,
    };
    addAddress(data);
  };

  const addAddress = ({}) => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    const body = JSON.stringify({
      first_name: firstName,
      last_name: lastName,
      phone_number: phone,
      delivery_address: address,
      state_or_region: state,
      city: city,
      default: defaultAddress,
    });

    authAxios
      .post(addAddressURL, body, config)
      .then((res) => {
        console.log(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const closeModal = () => {
    document.querySelector(".modal-overlay").classList.add("hide");
  };

  window.addEventListener("click", (e) => {
    if (e.target.classList.contains("modal-overlay")) {
      document.querySelector(".modal-overlay").classList.add("hide");
    }
  });

  return (
    <div className="modal-overlay hide add-address">
      <div className="modal-wrapper">
        <div className="modal-header">
          <span className="modal-title">Add New Address</span>
          <div className="modal-title__btn">
            <IoMdClose onClick={() => closeModal()} />
          </div>
        </div>
        <div className="underline" />
        <form onSubmit={(e) => handleSubmit(e)} className="modal-body">
          <div className="input-names">
            <div className="input-group">
              <label htmlFor="firstName">First Name *</label>
              <input
                required
                autoComplete="off"
                type="text"
                placeholder="Please enter your first name"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
              />
            </div>
            <div className="input-group">
              <label htmlFor="lastName">Last Name *</label>
              <input
                required
                autoComplete="off"
                type="text"
                placeholder="Please enter your last name"
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
              />
            </div>
          </div>
          <div className="input-group">
            <label htmlFor="lastName">Mobile phone number *</label>
            <div className="phone-input">
              <span className="phone-code">+254</span>
              <input
                required
                autoComplete="off"
                type="text"
                placeholder="700000030"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
              />
            </div>
          </div>
          <div className="input-group">
            <label htmlFor="lastName">Delivery Address *</label>
            <textarea
              required
              autoComplete="off"
              placeholder="Street Name / Building / Apartment No / Floor"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
          </div>
          <div className="input-group">
            <label htmlFor="region">State/Region *</label>
            <select
              value={state}
              onChange={(e) => setState(e.target.value)}
              required>
              <option value="0">Please select...</option>
              <option value="siaya">Siaya</option>
              <option value="siaya">Kisumu</option>
            </select>
          </div>
          <div className="input-group">
            <label htmlFor="city">City *</label>
            <select
              value={city}
              onChange={(e) => setCity(e.target.value)}
              required>
              <option value="0">Please select...</option>
              <option value="siaya">Siaya</option>
              <option value="siaya">Kisumu</option>
            </select>
          </div>
          <div className="input-default">
            <input
              onChange={() => setDefaultAddress(!defaultAddress)}
              type="checkbox"
            />
            <strong>Set as default address</strong>
          </div>
          <button type="submit" className="btn btn-primary">
            Save
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddAddress;
