import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { useTimeout } from "react-use";
import "./style.css";

const Alerts = ({ error, status, messages }) => {
  const [msg, setMsg] = useState("");
  const [mstStatus, setMstStatus] = useState(null);

  useEffect(() => {
    if (error.message) {
      setMsg(error.message);
      setMstStatus(status);
    }
    useTimeout(() => {
      if (mstStatus) {
        setMstStatus(null);
        setMsg("");
      }
    }, 3000);
  }, [error]);

  useEffect(() => {
    if (messages.saveMsg) {
      setMsg(messages.saveMsg);
      setMstStatus(200);
    }
    useTimeout(() => {
      if (mstStatus) {
        setMstStatus(null);
        setMsg("");
      }
    }, 3000);
  }, [messages]);

  return (
    <>
      {mstStatus && (
        <div className={mstStatus == 200 ? "alert success" : "alert error"}>
          <p>{msg}</p>
        </div>
      )}
    </>
  );
};

const mapStateToProps = (state) => ({
  error: state.errors.msg,
  status: state.errors.status,
  messages: state.messages,
});

export default connect(mapStateToProps)(Alerts);
