import React, { useEffect } from "react";
import { Container } from "semantic-ui-react";
import { fetchCart } from "../redux/action/cart";
import { Link } from "react-router-dom";
import img1 from "../assets/imgs/shampoo1.jpg";
import img2 from "../assets/imgs/shampoo2.jpg";
import AddressForm from "./AddressForm";
import PaymentMethod from "./PaymentMethod";
import { connect } from "react-redux";
import { getImage } from "../utils";

const Checkout = ({ fetchCart, cart }) => {
  useEffect(() => {
    fetchCart();
  }, []);

  document.title = `Checkout ( ${cart?.order_items.length} items ) | Luxury Silk Online`;
  return (
    <Container className="checkout">
      <div className="checkout_wrapper">
        <div className="checkout_content">
          <h3 className="checkout_title">checkout</h3>
          <div className="checkout_content--inner">
            <AddressForm />
            <PaymentMethod />
          </div>
        </div>
        <div className="checkout_order">
          <h3 className="checkout_title">order summary</h3>
          <div className="checkout_order--inner">
            <h3 className="checkout_order__title">
              your order{" "}
              {cart?.order_items.length > 1 ? (
                <span>( {cart?.order_items.length} items )</span>
              ) : (
                <span>( {cart?.order_items.length} item )</span>
              )}
            </h3>
            <div className="underline"></div>
            <div className="checkout_order-items">
              {cart?.order_items.map((item, i) => (
                <div key={i} className="checkout_order-item">
                  <img src={getImage(item.item_obj.image)} alt="" />
                  <div className="checkout_order-item__details">
                    <h4 className="checkout_order-item__title">
                      {item.item_obj.title}
                    </h4>
                    {item.item_obj.discount_price ? (
                      <h4 className="checkout_order-item__price">
                        KSh {item.item_obj.discount_price}
                      </h4>
                    ) : (
                      <h4 className="checkout_order-item__price">
                        KSh {item.item_obj.price}
                      </h4>
                    )}

                    <p>
                      <span className="text-muted">Qty: </span>
                      {item.quantity}
                    </p>
                  </div>
                </div>
              ))}
            </div>
            <div className="underline"></div>
            <div className="checkout_order-summary">
              <div className="checkout_order-summary_item">
                <h3>Subtotal</h3>
                <h3>KSh {cart?.total}</h3>
              </div>
              <div className="checkout_order-summary_item">
                <h3>VAT</h3>
                <h3>KSh 0</h3>
              </div>
              <div className="checkout_order-summary_item">
                <h3>Local delivery fees</h3>
                <h3>N.A.</h3>
              </div>
              <div className="underline"></div>
              <div className="checkout_order-summary_item">
                <strong>
                  <h3>Total</h3>
                </strong>
                <h3 className="checkout_order-summary_total">
                  KSh. {cart?.total}
                </h3>
              </div>
            </div>
            <div className="underline"></div>
            <div className="checkout_order-modify">
              <Link to="/order-summary">modify cart</Link>
            </div>
          </div>
        </div>
      </div>
    </Container>
  );
};

const mapStateToProps = (state) => ({
  cart: state.cart.shoppingCart,
});

export default connect(mapStateToProps, { fetchCart })(Checkout);
