import React, { useEffect, useState } from "react";
import { Container } from "semantic-ui-react";
import { IoMdTrash } from "react-icons/io";
import img from "../assets/imgs/moisturizer4.jpg";
import img2 from "../assets/imgs/moisturizer5.jpg";
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import { fetchCart } from "../redux/action/cart";
import { getImage } from "../utils";
import { handleRemoveItem } from "../redux/action/products";
import OrderItem from "./OrderItem";

const OrderSummary = ({ cart, fetchCart, handleRemoveItem }) => {
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    fetchCart();
  }, []);

  let items = cart?.order_items;

  document.title =
    cart != null
      ? `Shopping Cart (${
          cart?.order_items.length > 1
            ? `${cart?.order_items.length} items`
            : `${cart?.order_items.length} item`
        }) | Luxury Silk Online, High Quality Hair Products. Nairobi, Kenya.`
      : "Shopping Cart | Luxury Silk Online, High Quality Hair Products. Nairobi, Kenya.";

  return (
    <React.Fragment>
      <div className="title">
        <p className="title-nav">
          <Link to="/">Home</Link>/<span>Shopping Cart</span>
        </p>
        <h2 className="title-name">Your Shopping Cart</h2>
      </div>
      <div className="cart">
        <Container className="cart-wrapper">
          <h1 className="cart-title">
            Shopping Cart (
            {cart?.order_items.length > 1
              ? `${cart?.order_items.length} items`
              : `${cart?.order_items.length} item`}
            )
          </h1>
          <div className="cart-content">
            <div className="cart-header">
              <h3>item</h3>
              <h3>quantity</h3>
              <h3>unit price</h3>
              <h3>subtotal price</h3>
            </div>
            <div className="cart-body">
              {cart && cart?.map((item) => <OrderItem item={item} />)}
            </div>
          </div>
        </Container>
      </div>
    </React.Fragment>
  );
};

const mapStateToProps = (state) => ({
  cart: state.cart.shoppingCart,
});

export default connect(mapStateToProps, { fetchCart, handleRemoveItem })(
  OrderSummary
);
