import React, { useEffect, useState } from "react";
import { Button, Container, Select } from "semantic-ui-react";
import { fetchCart } from "../redux/action/cart";
import { Link, useHistory } from "react-router-dom";
import { connect } from "react-redux";
import { authAxios, getImage } from "../utils";
import { addAddress } from "../redux/action/address";
import { Redirect } from "react-router-dom";
import { FiChevronRight } from "react-icons/fi";
import { countryListURL } from "../constant";

const Checkout = ({ fetchCart, cart, addAddress, loading, success }) => {
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [apartment, setApartment] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [city, setCity] = useState("");
  const [countries, setCountries] = useState([]);
  const [country, setCountry] = useState("");
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchCart();
    handleFetchCountries();
  }, []);

  const handleFormatCountires = (countries) => {
    const keys = Object.keys(countries);
    return keys.map((k) => {
      return {
        key: k,
        text: countries[k],
        value: k,
      };
    });
  };

  const handleFetchCountries = () => {
    authAxios
      .get(countryListURL)
      .then((res) => {
        setCountries(handleFormatCountires(res.data));
      })
      .catch((err) => {
        setError(err);
      });
  };

  let cartItems = cart?.order_items;
  const history = useHistory();

  const handleSubmit = (e) => {
    const data = {
      firstname,
      lastname,
      phone,
      address,
      apartment,
      city,
      postalCode,
      country,
    };

    e.preventDefault();
    console.log(data);
  };

  if (cartItems?.length < 1) {
    return history.goBack();
  }

  if (success) {
    return <Redirect to="/shipping" />;
  }

  document.title = `Checkout ( ${cart?.order_items.length} items ) | Luxury Silk Online`;
  return (
    <>
      <Container className="checkout">
        <div className="checkout__wrapper">
          <div className="checkout-address">
            <div className="title-secondary">
              <h1>Luxury Silk</h1>
              <p className="title-nav">
                <Link to="/checkout" className="active">
                  Information
                </Link>{" "}
                <FiChevronRight /> <span>Shipping</span> <FiChevronRight />{" "}
                <span>Payment</span>
              </p>
            </div>
            <form className="shipping-form" onSubmit={(e) => handleSubmit(e)}>
              <div className="shipping-contact">
                <h3>Contact information</h3>
                <div className="input-group phone">
                  <label htmlFor="phone">Phone number</label>
                  <input
                    value={phone}
                    onChange={(e) => setPhone(e.target.value)}
                    type="text"
                  />
                </div>
              </div>
              <div className="shipping-data">
                <h3>Shipping address</h3>
                <div className="input-inline">
                  <div className="input-group firstname">
                    <label htmlFor="firstname">First name</label>
                    <input
                      value={firstname}
                      onChange={(e) => setFirstname(e.target.value)}
                      type="text"
                    />
                  </div>
                  <div className="input-group lastname">
                    <label htmlFor="lastname">Last name</label>
                    <input
                      type="text"
                      value={lastname}
                      onChange={(e) => setLastname(e.target.value)}
                    />
                  </div>
                </div>
                <div className="input-group lastname">
                  <label htmlFor="address">Address</label>
                  <input
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    type="text"
                  />
                </div>
                <div className="input-group lastname">
                  <label htmlFor="apartment">Apartment, suite, etc.</label>
                  <input
                    value={apartment}
                    onChange={(e) => setApartment(e.target.value)}
                    type="text"
                  />
                </div>
                <div className="input-group lastname">
                  <label htmlFor="city">City</label>
                  <input
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    type="text"
                  />
                </div>
                <div className="input-inline">
                  <div className="input-group firstname">
                    <label htmlFor="firstname">Country / Region</label>
                    <Select
                      loading={countries.length < 1}
                      fluid
                      search
                      clearable
                      options={countries}
                      onChange={(e) => setCountry(e.target.value)}
                    />
                  </div>
                  <div className="input-group lastname">
                    <label htmlFor="postalcode">Postal code</label>
                    <input
                      value={postalCode}
                      onChange={(e) => setPostalCode(e.target.value)}
                      type="text"
                    />
                  </div>
                </div>
              </div>
              <div className="checkout-nav">
                <Link to="/order-summary">
                  <i className="fa fa-chevron-left" />
                  Return to cart
                </Link>
                {cartItems?.length > 0 && (
                  <Button
                    loading={loading}
                    disabled={loading}
                    type="submit"
                    className="checkbox-btn">
                    Continue to shipping
                  </Button>
                )}
              </div>
            </form>
          </div>
          <div className="checkout-summary hide-mobile">
            <h3 className="checkout-title">Order summary</h3>
            {cartItems?.length === 0 && (
              <>
                <div className="checkout-summary__empty">
                  <p>Your cart is empty</p>
                  <Link to="/all-products">Continue Shopping</Link>
                </div>
              </>
            )}
            {cartItems?.length > 0 && (
              <>
                <div
                  className={
                    cart?.order_items.length > 3
                      ? "checkout-orders scroll"
                      : "checkout-orders"
                  }>
                  {cartItems &&
                    cartItems.map((item) => (
                      <div className="checkout-order">
                        <div className="checkout-order__img">
                          <img
                            src={getImage(item.item_obj.image)}
                            alt={item?.item}
                          />
                          <span>{item?.quantity}</span>
                        </div>
                        <div className="checkout-order__detail">
                          <h3>{item?.item}</h3>
                          <p className="text-muted">
                            {item?.item_obj.weight} / {item?.item_obj.category}{" "}
                            / Peppermint
                          </p>
                        </div>
                        <h3>KSh. {item?.final_price.toFixed(2)}</h3>
                      </div>
                    ))}
                </div>
                <div className="underline" />
                <div className="checkout-subtotal">
                  <div>
                    <h3 className="text-muted">Subtotal</h3>
                    <h3 className="checkout-subtotal__price">
                      KSh. {cart?.total.toFixed(2)}
                    </h3>
                  </div>
                  <div>
                    <h3 className="text-muted">Shipping</h3>
                    <p className="text-muted">KSh. 300.00</p>
                  </div>
                </div>
                <div className="underline" />
                <div className="checkout-total">
                  <h3>Total</h3>
                  <h2>KSh. {cart?.total.toFixed(2)}</h2>
                </div>
              </>
            )}
          </div>
        </div>
      </Container>
    </>
  );
};

const mapStateToProps = (state) => ({
  cart: state.cart.shoppingCart,
  loading: state.address.loading,
  success: state.address.success,
});

export default connect(mapStateToProps, { fetchCart, addAddress })(Checkout);
