import {
  CART_START,
  CART_SUCCESS,
  CART_FAIL,
  LOGOUT_SUCCESS,
  AUTH_ERROR,
  REMOVE_FROM_CART_SUCCESS,
} from "../types/index";

const initialState = {
  loading: false,
  shoppingCart: null,
  error: null,
};

export default function (state = initialState, { type, payload }) {
  switch (type) {
    case CART_START:
      return {
        ...state,
        error: null,
        loading: true,
      };
    case CART_SUCCESS:
      return {
        ...state,
        shoppingCart: payload,
        error: null,
        loading: false,
      };
    case CART_FAIL:
      return {
        ...state,
        error: payload,
        loading: false,
      };
    case REMOVE_FROM_CART_SUCCESS:
      return {
        ...state,
        shoppingCart: state.shoppingCart.order_items.filter(
          (item) => item.id !== payload
        ),
      };
    case LOGOUT_SUCCESS:
    case AUTH_ERROR:
      return {
        loading: false,
        shoppingCart: null,
        error: null,
      };
    default:
      return state;
  }
}
