import React, { useEffect } from "react";
import { Container } from "semantic-ui-react";
import { FaHeart, FaTrash, FaMinus, FaPlus } from "react-icons/fa";
import img from "../assets/imgs/1.jpg";
import img2 from "../assets/imgs/2.jpg";
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import { fetchCart } from "../redux/action/cart";
import { getImage } from "../utils";
import { handleRemoveItem } from "../redux/action/products";

const OrderSummary = ({ cart, fetchCart, handleRemoveItem }) => {
  useEffect(() => {
    fetchCart();
  }, []);

  document.title =
    cart != null
      ? `Cart (${cart.order_items.length} items) | Luxury Silk Online`
      : "Cart empty | Luxury Silk Online";

  const addQty = (e) => {
    let qty = e.target.parentElement.previousElementSibling.innerText;
    let value = parseInt(qty);
    console.log(value + 1);
  };

  return (
    <React.Fragment>
      <Container className="cart">
        <div className="cart__title">
          <h2>
            {cart != null
              ? `Cart (${cart.order_items.length} items)`
              : "Cart empty"}
          </h2>
        </div>
        <div className="cart__content">
          <div className="cart__content-title hide-mobile">
            <h2>item</h2>
            <h2>quantity</h2>
            <h2>unit price</h2>
            <h2>subtotal</h2>
          </div>
          {cart && (
            <>
              {cart?.order_items.map((order_item, i) => (
                <div key={i} className="cart__content-items">
                  <div className="cart_product">
                    <div className="cart_product-img">
                      <img src={getImage(order_item?.item_obj.image)} alt="" />
                    </div>
                    <div className="cart_product-info">
                      <h4>{order_item.item}</h4>
                      <div className="cart_product-ctrl">
                        <div>
                          <FaHeart />
                          <h4>move to saved items</h4>
                        </div>
                        <div
                          onClick={() => {
                            console.log(order_item.id);
                            handleRemoveItem(order_item.id);
                          }}>
                          <FaTrash
                            onClick={() => {
                              console.log(order_item.id);
                              handleRemoveItem(order_item.id);
                            }}
                          />
                          <h4>remove</h4>
                        </div>
                      </div>
                    </div>
                  </div>
                  <div className="cart_quantity hide-mobile">
                    <div className="quantity">
                      <FaMinus />
                      <span className="qty-value">{order_item.quantity}</span>

                      <FaPlus onClick={(e) => addQty(e)} />
                    </div>
                  </div>
                  <div className="cart_price hide-mobile">
                    {order_item.item_obj.discount_price ? (
                      <h4>KSh {order_item.item_obj.discount_price}</h4>
                    ) : (
                      <h4>KSh {order_item.item_obj.price}</h4>
                    )}
                    {order_item.item_obj.discount_price ? (
                      <>
                        <span>KSh {order_item.item_obj.price}</span>
                        <p>Saving: KSh {order_item.amount_saved}</p>
                      </>
                    ) : (
                      ""
                    )}
                  </div>
                  <div className="cart_subtotal hide-mobile">
                    <h3>KSh {order_item.final_price}</h3>
                  </div>
                </div>
              ))}
            </>
          )}

          {cart?.order_items.length < 1 && (
            <p className="cart__content-items">No items</p>
          )}
        </div>
        {cart?.order_items.length > 0 && (
          <div className="cart_total">
            <div className="cart_total-content">
              <div className="cart_total-item">
                <p>Subtotal:</p>
                <h4>KSh {cart?.total}</h4>
              </div>
              <div className="cart_total-item">
                <p>VAT:</p>
                <h4>KSh 0</h4>
              </div>
              <div className="cart_total-item">
                <p className="text-muted">Shipping fee:</p>
                <h4>+ KSh 200</h4>
              </div>
              <div className="underline" />
              <div className="cart_total-item final">
                <p>Total:</p>
                <h4>KSh {cart?.total}</h4>
              </div>
            </div>
          </div>
        )}
      </Container>
      <div className="cart_footer">
        <Container className="cart_footer-content">
          {cart?.order_items.length > 0 && (
            <Link to="/checkout" className="btn btn-primary">
              proceed to checkout
            </Link>
          )}
          <Link to="/products" className="btn btn-secondary">
            continue shopping
          </Link>
        </Container>
      </div>
    </React.Fragment>
  );
};

const mapStateToProps = (state) => ({
  cart: state.cart.shoppingCart,
});

export default connect(mapStateToProps, { fetchCart, handleRemoveItem })(
  OrderSummary
);
