import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import logo from "../../assets/imgs/logo.jpg";
import "./style.css";

const Nav = () => {
  // useEffect(() => {
  //   const stickyEl = document.querySelector(".sticky");
  //   const stickyPos = stickyEl.getBoundingClientRect().top;
  //   const offset = -20;

  //   console.log(stickyEl);

  //   window.addEventListener("scroll", () => {
  //     if (window.pageXOffset >= stickyPos + offset) {
  //       stickyEl.style.position = "fixed";
  //       stickyEl.style.top = "0px";
  //     } else {
  //       stickyEl.style.position = "static";
  //       stickyEl.style.top = "";
  //     }
  //   });
  // }, []);

  return (
    <div id="nav" className="nav">
      <div className="nav-top">
        <div className="nav-top__social">
          <Link to="">
            <i className="fa fa-facebook-f" />
          </Link>
          <Link to="">
            <i className="fa fa-instagram" />
          </Link>
          <Link to="">
            <i className="fa fa-twitter" />
          </Link>
        </div>
        <div className="nav-top__info">
          <p>
            For all orders made, the standard delivery timeline is between 24-72
            hrs.
          </p>
        </div>
        <div className="nav-top__links">
          <i className="fa fa-search"></i>
          <Link to="/">
            <i className="fa fa-home" />
          </Link>
          <Link to="/">
            <i className="fa fa-user" />
          </Link>
          <Link to="/">
            My Cart
            <i className="fa fa-shopping-cart" /> 1
          </Link>
        </div>
      </div>
      <div className="nav-mid">
        <Link to="/">
          <img src={logo} alt="logo" className="logo" />
        </Link>
      </div>
      <div className="nav-main">
        <ul className="menu">
          <li>
            <Link to="">Home</Link>
          </li>
          <li className="dropdown">
            <Link className="dropbtn" to="#">
              Shop
              <i className="fa fa-chevron-down" />
            </Link>
            <div class="dropdown-content">
              <a href="#">Link 1</a>
              <a href="#">Link 2</a>
              <a href="#">Link 3</a>
            </div>
          </li>
          <li>
            <Link to="">About us</Link>
          </li>
          <li>
            <Link to="">Contact us</Link>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Nav;
