import React, { useEffect } from "react";
import { Button, Container } from "semantic-ui-react";
import { fetchCart } from "../redux/action/cart";
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import { getImage } from "../utils";

const Payment = ({ fetchCart, cart }) => {
  useEffect(() => {
    fetchCart();
  }, []);

  let cartItems = cart?.order_items;

  if (cartItems?.length < 1) {
    return <Redirect to="/" />;
  }

  document.title = `Payment ( KSh. ${cart?.total}  ) | Luxury Silk Online`;
  return (
    <>
      <div className="title">
        <p className="title-nav">
          <Link to="/">Home</Link>/
          <Link to="/order-summary">Shopping Cart</Link>/
          <Link to="/checkout">Checkout</Link>/<span>Payment</span>
        </p>
        <h2 className="title-name">Payment</h2>
      </div>
      <Container className="checkout">
        <div className="checkout__wrapper">
          <div className="payment-info">
            <div className="payment-info__user"></div>
            <h3 className="checkout-title">Payment</h3>
          </div>
          <div className="checkout-summary">
            <h3 className="checkout-title">Order summary</h3>
            <div
              className={
                cart?.order_items.length > 3
                  ? "checkout-orders scroll"
                  : "checkout-orders"
              }>
              {cartItems &&
                cartItems.map((item) => (
                  <div className="checkout-order">
                    <div className="checkout-order__img">
                      <img
                        src={getImage(item.item_obj.image)}
                        alt={item?.item}
                      />
                      <span>{item?.quantity}</span>
                    </div>
                    <div className="checkout-order__detail">
                      <h3>{item?.item}</h3>
                      <p className="text-muted">
                        {item?.item_obj.weight} / {item?.item_obj.category} /{" "}
                        Peppermint
                      </p>
                    </div>
                    <h3>KSh. {item?.final_price}</h3>
                  </div>
                ))}
            </div>
            <div className="underline" />
            <div className="checkout-subtotal">
              <div>
                <h3 className="text-muted">Subtotal</h3>
                <h3 className="checkout-subtotal__price">KSh. {cart?.total}</h3>
              </div>
              <div>
                <h3 className="text-muted">Shipping</h3>
                <p className="text-muted">Calculated at next step</p>
              </div>
            </div>
            <div className="underline" />
            <div className="checkout-total">
              <h3>Total</h3>
              <h2>KSh. {cart?.total}</h2>
            </div>
          </div>
        </div>
      </Container>
    </>
  );
};

const mapStateToProps = (state) => ({
  cart: state.cart.shoppingCart,
});

export default connect(mapStateToProps, { fetchCart })(Payment);
