import React, { useState } from "react";
import { Container } from "semantic-ui-react";
import { Link, useHistory, useLocation } from "react-router-dom";
import {
  FiSearch,
  FiUser,
  FiUserCheck,
  FiHelpCircle,
  FiShoppingCart,
  FiLogOut,
  FiShoppingBag,
} from "react-icons/fi";
import { logOut } from "../../redux/action/auth";
import logo from "../../assets/imgs/logo.jpg";
import { connect } from "react-redux";
import { searchItems } from "../../redux/action/products";
import "./style.css";
import { FaArchive, FaHeart } from "react-icons/fa";
import { authAxios } from "../../utils";
import { orderSummaryURL } from "../../constant";
import { useEffect } from "react";

const Nav = ({ auth, cart, logOut, searchItems }) => {
  const { pathname } = useLocation();
  const history = useHistory();
  const [searchWord, setSearchWord] = useState("");
  const [shoppingBag, setShoppingBag] = useState(null);

  useEffect(() => {
    getCart();
    return () => {
      getCart();
    };
  }, []);

  const getCart = () => {
    authAxios
      .get(orderSummaryURL)
      .then((res) => {
        setShoppingBag(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const userLinks = () => {
    document.querySelector(".nav-dropdown.left").classList.toggle("hide");
  };

  console.log(localStorage.getItem("itemsTotal"));
  let items = 0;

  items += localStorage.getItem("itemsTotal");

  const handleSubmit = (e) => {
    e.preventDefault();
    history.push("/products");
    searchItems(searchWord);
  };

  const helpLinks = () => {
    document.querySelector(".nav-dropdown.right").classList.toggle("hide");
  };

  return (
    <>
      {pathname !== "/login" && pathname !== "/register" && pathname !== "/" && (
        <div className="nav" id="nav">
          <Container className="nav-content">
            <div className="nav-logo">
              <Link to="/">
                <img src={logo} alt="Luxury Suite" />
                <h5>luxury silk</h5>
              </Link>
            </div>
            <div className="nav-search">
              <form onSubmit={(e) => handleSubmit(e)}>
                <div className="search-content">
                  <FiSearch />
                  <input
                    type="text"
                    value={searchWord}
                    placeholder="Search products, brands and categories"
                    onChange={(e) => setSearchWord(e.target.value)}
                  />
                </div>
                <button>search</button>
              </form>
            </div>
            <div className="nav-info">
              <div className="nav-icon">
                {auth?.username ? (
                  <>
                    <FiUserCheck className="nav-dropdown__svg" />
                    <h5 onClick={() => userLinks()}>
                      {auth?.username.length > 7
                        ? `${auth?.username.substring(0, 7)}...`
                        : auth?.username}
                    </h5>
                    <i
                      className="fa fa-chevron-down"
                      onClick={() => userLinks()}
                    />
                    <ul className="nav-dropdown left hide">
                      <li
                        onClick={() => history.push("/account")}
                        className="nav-dropdown-item">
                        <FiUser /> Account
                      </li>
                      <li className="nav-dropdown-item">
                        <FaArchive /> Orders
                      </li>
                      <li className="nav-dropdown-item">
                        <FaHeart /> Saved Items
                      </li>
                      <div className="underline" />
                      <li className="nav-logout" onClick={() => logOut()}>
                        Logout
                      </li>
                    </ul>
                  </>
                ) : (
                  <>
                    <FiUser />
                    <Link to="/login">
                      login
                      <i className="fa fa-chevron-down" />
                    </Link>
                  </>
                )}
              </div>
              {/* <Link to="/login" className="nav-icon">
                <FiHelpCircle className="nav-dropdown__svg" />
                <h5 onClick={() => helpLinks()}>Help</h5>
                <i onClick={() => helpLinks()} className="fa fa-chevron-down" />
                <ul className="nav-dropdown right hide">
                  <li className="nav-dropdown-item">List Item</li>
                  <li className="nav-dropdown-item">List Item</li>
                  <div className="underline" />
                  <li className="nav-dropdown-item">List Item</li>
                  <li className="nav-dropdown-item">
                    <FiLogOut /> Logout
                  </li>
                </ul>
              </Link> */}
              <Link to="/order-summary" id="cart-btn" className="nav-icon ">
                <FiShoppingCart className="nav-dropdown__svg" />
                <h5>Cart</h5>
                <span className="cart-items">
                  {cart ? cart?.order_items.length : 0}
                </span>
              </Link>
            </div>
          </Container>
        </div>
      )}
      {pathname === "/" && (
        <div className="nav-home" id="nav">
          <div className="nav_info">
            <p>The standard delivery timeline is between 24 - 72hrs.</p>
          </div>
          <Container className="nav_wrapper">
            <Link to="/" className="nav_wrapper-logo">
              <img src={logo} alt="luxury silk" />
              <h3>
                luxury <br />
                silk
              </h3>
            </Link>
            <ul className="nav_wrapper-content">
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/products">our products</Link>
              </li>
              <li>
                <Link to="/about">about us</Link>
              </li>
              <li>
                <Link to="/contact">contact us</Link>
              </li>
            </ul>
            {auth?.username ? (
              <div className="nav_wrapper-auth_verified">
                <Link to="/account" className="nav_wrapper-info_item">
                  <FiUserCheck />
                </Link>
                <Link to="/order-summary" className="nav_wrapper-info_item">
                  <FiShoppingBag />
                  <span className="cart-items">
                    {cart ? cart?.order_items.length : 0}
                  </span>
                </Link>
              </div>
            ) : (
              <div className="nav_wrapper-auth">
                <Link to="/login">
                  <p>Sign in</p>
                </Link>
              </div>
            )}
          </Container>
        </div>
      )}
    </>
  );
};

const mapStateToProps = (state) => ({
  auth: state.auth.user,
  cart: state.cart.shoppingCart,
});

export default connect(mapStateToProps, { logOut, searchItems })(Nav);
