import React, { useEffect } from "react";
import Products from "./layouts/Products";
import ProductDetail from "./layouts/ProductDetail";
import "semantic-ui-css/semantic.min.css";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import "./App.css";
import "./mobile.css";
import "./imports.css";
import Nav from "./component/Nav/Nav";
import Home from "./layouts/Home";
import OrderSummary from "./layouts/OrderSummary";
import Checkout from "./layouts/Checkout";
import Cart from "./component/cart/Cart";
import Login from "./layouts/Login";
import Register from "./layouts/Register";
import { getUser } from "./redux/action/auth";
import { fetchCart } from "./redux/action/cart";
import { getItems } from "./redux/action/products";
import { connect } from "react-redux";
import PrivateRouter from "./auth/PrivateRouter";
import ResetPass from "./layouts/ResetPass";
import "./assets/css/font-awesome/css/font-awesome.min.css";
import Account from "./layouts/Account";
import { useLocation } from "react-use";
import Footer from "./component/Footer/Footer";
import ScrollToTop from "./component/ScrollToTop";
import Error from "./layouts/Error";
import AddAddress from "./modals/AddAddress/AddAddress";
import QuickView from "./modals/QuickView/QuickView";

function App({ products, getUser, fetchCart, getItems }) {
  useEffect(() => {
    fetchCart();
    getItems();
  }, []);

  let items = [];

  items = products?.results;

  console.log(items);

  const getShampoo = (products) => {
    let shampoo = products?.filter((item) => {
      item.category = "Shampoo";
    });
    console.log(shampoo);
  };

  getShampoo(items);

  const location = useLocation();

  return (
    <React.Fragment>
      <Router>
        <Nav />
        <AddAddress />
        <QuickView />
        <Switch>
          <Route exact path="/" component={Home} />
          <Route exact path="/login" component={Login} />
          <Route exact path="/register" component={Register} />
          <Route exact path="/reset-password" component={ResetPass} />
          <Route exact path="/collections" component={Products} />
          <Route exact path="/collections/:slug" component={ProductDetail} />
          <PrivateRouter exact path="/account" component={Account} />
          <PrivateRouter exact path="/order-summary" component={OrderSummary} />
          <PrivateRouter exact path="/checkout" component={Checkout} />
          <Route component={Error} />
        </Switch>
        {location.pathname !== "/login" &&
          location.pathname !== "/reset-password" &&
          location.pathname !== "/register" && <Footer />}
      </Router>
    </React.Fragment>
  );
}

const mapStateToProps = (state) => ({
  products: state.products.products,
});

export default connect(mapStateToProps, { getUser, fetchCart, getItems })(App);
