import React, { useState, useEffect } from "react";
import { FiCheckCircle } from "react-icons/fi";
import { Button } from "semantic-ui-react";
import { connect } from "react-redux";
import { saveAddress } from "../redux/action/address";
import { authAxios } from "../utils";
import { addressURL } from "../constant";

const AddressForm = ({ saveAddress }) => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [phone, setPhone] = useState("");
  const [extra, setExtra] = useState("");
  const [address, setAddress] = useState("");
  const [state, setState] = useState("Nairobi");
  const [city, setCity] = useState("Nairobi");

  const [loading, setLoading] = useState(false);
  const [userAddress, setUserAddress] = useState(null);
  const [error, setError] = useState(null);

  const [saved, setSaved] = useState(true);
  const [show, setShow] = useState(false);

  useEffect(() => {
    fetchAddress();
  }, []);

  console.log(userAddress);

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = { firstName, lastName, phone, extra, address, state, city };
    setSaved(false);
    saveAddress(data);
    setShow(true);
  };

  const fetchAddress = () => {
    setLoading(true);
    authAxios
      .get(addressURL)
      .then((res) => setUserAddress(res.data))
      .catch((err) => {
        setError(err);
      });
  };

  return (
    <div className="checkout_content-item">
      <div className="checkout_content-item__title">
        <h3>
          <FiCheckCircle />
          1. address details
        </h3>
        <div className="saved">
          {saved && <h4 onClick={() => setShow(!show)}>change</h4>}
        </div>
      </div>
      <div className="underline" />
      <div className="address_list">
        {userAddress &&
          userAddress?.results.map((address) => (
            <div className="address_closed">
              <h4>Maurice Oluoch</h4>
              <p className="text-muted">P.O Box 144 Siaya, Siaya Town</p>
              <p className="text-muted">+254795772333</p>
            </div>
          ))}
      </div>
    </div>
  );
};

export default connect(null, { saveAddress })(AddressForm);
