import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { getImage } from "../utils";
import { FiMinus, FiPlus } from "react-icons/fi";
import { handleRemoveFromCart } from "../redux/action/products";

const OrderItem = ({ item, handleRemoveFromCart }) => {
  const [quantity, setQuantity] = useState(null);

  useEffect(() => {
    setQuantity(item.quantity);
  }, []);

  const addCart1 = (
    <div className="oil-product-qty">
      <span>Quantity</span>
      <FiMinus
        className="qty-down"
        onClick={() => {
          quantity > 1 ? setQuantity(quantity - 1) : setQuantity(1);
        }}
      />
      <span className="qty-value">{quantity}</span>
      <FiPlus className="qty-up" onClick={() => setQuantity(quantity + 1)} />
    </div>
  );

  const addCart2 = (
    <div className="cart-item__add">
      <FiMinus
        onClick={() => (quantity > 1 ? setQuantity(quantity - 1) : "")}
      />
      <span className="cart-item__quantity">{quantity}</span>
      <FiPlus onClick={() => setQuantity(quantity + 1)} />
    </div>
  );

  return (
    <>
      <div className="cart-item">
        <div className="cart-item__detail">
          <img src={getImage(item?.item_obj.image)} alt={item.item} />
          <div>
            <span className="text-muted">
              {item?.item_obj.category}/{item?.item_obj.hair_type}
            </span>
            <h3>{item?.item}</h3>
            <div
              onClick={() => handleRemoveFromCart(item?.item_obj.ref_code)}
              className="remove">
              <i className="fa fa-trash" /> remove
            </div>
          </div>
        </div>
        <div className="cart-item__qty">{addCart2}</div>
        <div className="cart-item__price">
          <h3>KES {item?.item_obj.price}</h3>
        </div>
        <div className="cart-item__subtotal">
          <h3>KES {(quantity * item?.item_obj.price).toFixed(2)}</h3>
        </div>
      </div>
    </>
  );
};

export default connect(null, { handleRemoveFromCart })(OrderItem);
