import {
  LOGIN_ERROR,
  LOGIN_LOADING,
  LOGIN_SUCCESS,
  LOGOUT_LOADING,
  LOGOUT_SUCCESS,
  RESET_PASS_LOADING,
  RESET_PASS_SUCCESS,
  USER_LOADED,
  AUTH_ERROR,
  USER_LOADING,
} from "../types";

const initialState = {
  token: localStorage.getItem("token"),
  loading: false,
  user: null,
  isAuthenticated: false,
  error: null,
};

export default function (state = initialState, { type, payload }) {
  switch (type) {
    case USER_LOADING:
    case LOGIN_LOADING:
    case LOGOUT_LOADING:
      return {
        ...state,
        loading: true,
      };
    case USER_LOADED:
      return {
        ...state,
        loading: false,
        isAuthenticated: true,
        user: payload,
      };
    case LOGIN_SUCCESS:
      localStorage.setItem("token", payload.token);
      return {
        ...state,
        ...payload,
        loading: false,
        user: payload,
        isAuthenticated: true,
      };
    case LOGIN_ERROR:
    case LOGOUT_SUCCESS:
    case AUTH_ERROR:
      localStorage.removeItem("token");
      return {
        ...state,

        loading: false,
        user: null,
        token: null,
        isAuthenticated: false,
        error: payload,
      };

    default:
      return state;
  }
}
