import React, { useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { Button, Container } from "semantic-ui-react";
import {
  FaFacebookF,
  FaTwitter,
  FaChevronLeft,
  FaMinus,
  FaPlus,
  FaPinterestP,
} from "react-icons/fa";
import { FiHeart, FiShoppingCart, FiChevronLeft } from "react-icons/fi";
import { getItemDetail, handleAddToCart } from "../redux/action/products";
import { connect } from "react-redux";
import img1 from "../assets/imgs/shampoo1.jpg";
import img2 from "../assets/imgs/shampoo2.jpg";
import img3 from "../assets/imgs/shampoo3.jpg";
import img4 from "../assets/imgs/shampoo4.jpg";
import img5 from "../assets/imgs/shampoo5.jpg";
import { getDiscountPerc, getImage } from "../utils";

const ProductDetail = ({
  getItemDetail,
  handleAddToCart,
  auth,
  product: { item, loading, error },
}) => {
  const { location } = useHistory();
  let slug = location.pathname;

  let itemSlug = `/products/${slug.split("/").slice(3, 4).toString()}`;

  useEffect(() => {
    getItemDetail(itemSlug);
  }, [itemSlug]);

  document.title = item
    ? `${item.title} | Luxury Silk Online`
    : "Luxury Silk Online";

  const history = useHistory();

  const checkUser = (code) => {
    if (auth.token === null) {
      return history.push("/login");
    } else {
      return handleAddToCart(code);
    }
  };

  console.log(item);

  return (
    <>
      <div className="title">
        <p className="title-nav">
          <Link to="/">Home</Link>/<Link to="/collections">Collections</Link>/
          <Link to="/collections/all-products">All products</Link>/
          <span>{item.title}</span>
        </p>
        <h2 className="title-name">{item.title}</h2>
      </div>
      <Container>
        <div className="product">
          <div className="product_info">
            <div className="product_info-left"></div>
            <div className="product_info-right">
              <div className="product_info-right__img">
                <img src={getImage(item.image)} alt={item.title} />
              </div>
              <div className="product_info-right__info"></div>
            </div>
          </div>

          <div className="product_desc">
            <h3 className="product_desc-title">Description</h3>
            <div className="underline" />
            <div className="product_desc-item">
              <h4>directions of use:</h4>
              <p>
                On damp hair, separate into sections depending on volume and
                length. If hair is dry, spritz lightly with our{" "}
                <strong>"Miracle Mist" Daily Spritz</strong> to soften and
                dampen. Apply <strong>"Curl Magic!" Curling Gel </strong>
                liberally sections by section from root to tip. Curls will
                appear instantly! Let hair air-dry. Perfect for wash & go's, the
                curling sponge, rod-sets, finger coils, Bantu knots, twists,
                braids, and all other styles! If twist-outs or braid-outs, take
                down when completely dry for stronger definition.
              </p>
            </div>
            <div className="product_desc-item">
              <h4>unisex & great for all!</h4>
              <p>
                Women. Men. Kids. Naturals and Trasitioners. (This product is
                not suitable for chemically treated or relaxed hair.)
              </p>
            </div>
            <div className="product_desc-item">
              <h4>Ingredients:</h4>
              <p>
                Deionized Water, Glycerin, Carbomer Solution, Provitamin B5,
                Flax Seed Gel, Aloe Vera Gel, Sunflower Oil, Peppermint Leaves,
                Phenoxyethanol, Berries & Fruit Extract.
              </p>
            </div>
          </div>
          <div className="product_related">
            <h2>you may also like</h2>
            <div className="underline" />
            <div className="product_related-items">
              <div className="product_related-item">
                <Link to="">
                  <img src={img1} alt="" />
                </Link>
                <div>
                  <Link to="">
                    <h3>"CURL ME PRETTY" CURLIN...</h3>
                  </Link>
                  <h4>KSH 2,000</h4>
                </div>
              </div>
              <div className="product_related-item">
                <Link to="">
                  <img src={img2} alt="" />
                </Link>
                <div>
                  <Link to="">
                    <h3>"CURL ME PRETTY" CURLIN...</h3>
                  </Link>
                  <h4>KSH 2,000</h4>
                </div>
              </div>
              <div className="product_related-item">
                <Link to="">
                  <img src={img3} alt="" />
                </Link>
                <div>
                  <Link to="">
                    <h3>"CURL ME PRETTY" CURLIN...</h3>
                  </Link>
                  <h4>KSH 2,000</h4>
                </div>
              </div>
              <div className="product_related-item">
                <Link to="">
                  <img src={img4} alt="" />
                </Link>
                <div>
                  <Link to="">
                    <h3>"CURL ME PRETTY" CURLIN...</h3>
                  </Link>
                  <h4>KSH 2,000</h4>
                </div>
              </div>
            </div>
            <Link to="/products" className="product_related-back">
              <FaChevronLeft /> Back to PRODUCTS
            </Link>
          </div>
        </div>
      </Container>
    </>
  );
};

const maptStateToProps = (state) => ({
  product: state.product,
  auth: state.auth,
});

export default connect(maptStateToProps, { handleAddToCart, getItemDetail })(
  ProductDetail
);
