import React, { useEffect, useState } from "react";
import { Container } from "semantic-ui-react";
import { IoMdClose } from "react-icons/io";
import img from "../assets/imgs/moisturizer4.jpg";
import img2 from "../assets/imgs/moisturizer5.jpg";
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import { fetchCart } from "../redux/action/cart";
import { getImage } from "../utils";
import { handleRemoveItem } from "../redux/action/products";

const OrderSummary = ({ cart, fetchCart, handleRemoveItem }) => {
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    fetchCart();
  }, []);

  let items = cart?.order_items;
  console.log(items);

  document.title =
    cart != null
      ? `Cart (${cart.order_items.length} items) | Luxury Silk Online, High Quality Hair Products. Nairobi, Kenya.`
      : "Cart empty | Luxury Silk Online, High Quality Hair Products. Nairobi, Kenya.";

  const addQty = (e) => {
    let qty = e.target.parentElement.previousElementSibling.innerText;
    let value = parseInt(qty);
    console.log(value + 1);
  };

  return (
    <React.Fragment>
      <div className="title">
        <p className="title-nav">
          <Link to="/">Home</Link>/<span>Shopping Cart</span>
        </p>
        <h2 className="title-name">Your Shopping Cart</h2>
      </div>
      <Container className="cart">
        <div className="cart-wrapper">
          <div className="cart-products">
            <div className="cart-title">
              <h3>Products</h3>
            </div>
            <div className="cart-items">
              <div className="cart-item">
                <div className="cart-item__img">
                  <IoMdClose />
                  <img src={img} alt="" />
                </div>
                <div className="cart-item__info">
                  <h3>Shampoo Luxury Silk</h3>
                  <p>200g / Normal / Apricot</p>
                  <h4>$220.00</h4>
                  <div className="product-add">
                    <div className="qty-selection">
                      <a
                        onClick={() =>
                          quantity > 1 ? setQuantity(quantity - 1) : ""
                        }
                        field="quantity"
                        className="down quantity-control-down">
                        -
                      </a>
                      <input
                        type="text"
                        min="1"
                        name="quantity"
                        value={quantity}
                        onChange={(e) => setQuantity(e.target.value)}
                        className="quantity"
                      />
                      <a
                        onClick={() => setQuantity(quantity + 1)}
                        field="quantity"
                        className="up quantity-control-up">
                        +
                      </a>
                    </div>
                  </div>
                  <h4 className="cart-item__total">Total: $2200.00</h4>
                </div>
              </div>
              <div className="underline" />
              <div className="cart-item">
                <div className="cart-item__img">
                  <IoMdClose />
                  <img src={img} alt="" />
                </div>
                <div className="cart-item__info">
                  <h3>Shampoo Luxury Silk</h3>
                  <p>200g / Normal / Apricot</p>
                  <h4>$220.00</h4>
                  <div className="product-add">
                    <div className="qty-selection">
                      <a
                        onClick={() =>
                          quantity > 1 ? setQuantity(quantity - 1) : ""
                        }
                        field="quantity"
                        className="down quantity-control-down">
                        -
                      </a>
                      <input
                        type="text"
                        min="1"
                        name="quantity"
                        value={quantity}
                        onChange={(e) => setQuantity(e.target.value)}
                        className="quantity"
                      />
                      <a
                        onClick={() => setQuantity(quantity + 1)}
                        field="quantity"
                        className="up quantity-control-up">
                        +
                      </a>
                    </div>
                  </div>
                  <h4 className="cart-item__total">Total: $2200.00</h4>
                </div>
              </div>
              <div className="underline" />
            </div>
          </div>
          <div className="cart-summary">
            <div className="cart-title">
              <h3>Order Summary</h3>
            </div>
          </div>
        </div>
      </Container>
    </React.Fragment>
  );
};

const mapStateToProps = (state) => ({
  cart: state.cart.shoppingCart,
});

export default connect(mapStateToProps, { fetchCart, handleRemoveItem })(
  OrderSummary
);
