import React, { useState, useEffect } from "react";
import { getImage } from "../utils";
import { IoMdTrash } from "react-icons/io";

const OrderItem = ({ item }) => {
  const [quantity, setQuantity] = useState(null);

  useEffect(() => {
    setQuantity(item.quantity);
  }, []);

  const addCart1 = (
    <div className="product_info-right__add">
      <div className="product_info-right__qty"></div>
    </div>
  );

  const addCart2 = (
    <div className="product-add">
      <div className="qty-selection">
        <a
          onClick={() => (quantity > 1 ? setQuantity(quantity - 1) : "")}
          field="quantity"
          className="down quantity-control-down">
          -
        </a>
        <span className="quantity">{quantity}</span>
        <a
          onClick={() => setQuantity(quantity + 1)}
          field="quantity"
          className="up quantity-control-up">
          +
        </a>
      </div>
    </div>
  );

  return (
    <>
      <div className="cart-item">
        <div className="cart-item__detail">
          <img src={getImage(item?.item_obj.image)} alt={item.item} />
          <div>
            <p className="text-muted">
              {item?.item_obj.category}/{item?.item_obj.hair_type}
            </p>
            <h3>{item?.item}</h3>
          </div>
        </div>
        <div className="cart-item__qty">{addCart2}</div>
        <div className="cart-item__price">
          <h3>KES {item?.item_obj.price}</h3>
        </div>
        <div className="cart-item__subtotal">
          <h3>KES {quantity * item?.item_obj.price}</h3>
        </div>
      </div>
    </>
  );
};

export default OrderItem;
