import React, { useEffect, useState } from "react";
import { Button, Container, Message } from "semantic-ui-react";
import { fetchCart } from "../redux/action/cart";
import { Link, useHistory } from "react-router-dom";
import { connect } from "react-redux";
import { authAxios, getImage, config } from "../utils";
import { addAddress } from "../redux/action/address";
import { Redirect } from "react-router-dom";
import { FiChevronRight } from "react-icons/fi";
import {
  countryListURL,
  addAddressURL,
  shippingSummaryURL,
  updateAddressURL,
} from "../constant";

const Checkout = ({ fetchCart, cart, addAddress }) => {
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [contact, setContact] = useState("");
  const [address, setAddress] = useState("");
  const [apartment, setApartment] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [city, setCity] = useState("");
  const [country, setCountry] = useState("");
  const [countries, setCountries] = useState([]);
  const [makeDefault, setMakeDefault] = useState(false);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [details, setDetails] = useState(null);
  const [update, setUpdate] = useState(false);

  useEffect(() => {
    fetchCart();
    handleFetchCountries();
    getShippingSummary();
  }, []);

  useEffect(() => {
    if (details) {
      setUpdate(true);
    } else {
      setUpdate(false);
    }

    setFirstname(details?.first_name);
    setLastname(details?.last_name);
    setContact(details?.contact);
    setAddress(details?.address);
    setApartment(details?.apartment);
    setPostalCode(details?.postal_code);
    setCity(details?.city);
    setCountry(details?.country_or_region);
    setMakeDefault(details?.default);
  }, [details]);

  console.log(details);

  const getShippingSummary = () => {
    authAxios
      .get(shippingSummaryURL)
      .then((res) => {
        setDetails(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  console.log(details);

  const handleFormatCountires = (countries) => {
    const keys = Object.keys(countries);
    return keys.map((k) => {
      return {
        key: k,
        text: countries[k],
        value: k,
      };
    });
  };

  const handleFetchCountries = () => {
    authAxios
      .get(countryListURL)
      .then((res) => {
        setCountries(handleFormatCountires(res.data));
      })
      .catch((err) => {
        setError(err);
      });
  };

  const handleSubmitAddress = (e) => {
    e.preventDefault();
    setSaving(true);

    const data = {
      contact,
      first_name: firstname,
      last_name: lastname,
      address,
      apartment,
      city,
      country_or_region: country,
      postal_code: postalCode,
      default: makeDefault,
    };

    authAxios
      .post(
        update ? updateAddressURL(details?.id) : addAddressURL,
        data,
        config
      )
      .then((res) => {
        setSaving(false);
        setSuccess(true);
      })
      .catch((err) => setError(err));
  };

  let cartItems = cart?.order_items;
  const history = useHistory();

  if (cartItems?.length < 1) {
    return history.goBack();
  }

  if (success) {
    return <Redirect to="/shipping" />;
  }

  document.title = `Checkout ( ${cart?.order_items.length} items ) | Luxury Silk Online`;
  return (
    <>
      <Container className="checkout">
        <div className="checkout__wrapper">
          <div className="checkout-address">
            <div className="title-secondary">
              <h1>Luxury Silk</h1>
              <p className="title-nav">
                <Link to="/checkout" className="active">
                  Information
                </Link>{" "}
                <FiChevronRight /> <span>Shipping</span> <FiChevronRight />{" "}
                <span>Payment</span>
              </p>
            </div>
            <form
              className="shipping-form"
              onSubmit={(e) => handleSubmitAddress(e)}>
              <div className="shipping-contact">
                <h3>Contact information</h3>
                <div className="input-group contact">
                  <label htmlFor="contact">Email or mobile number</label>
                  <input
                    value={contact}
                    onChange={(e) => setContact(e.target.value)}
                    type="text"
                  />
                </div>
              </div>
              <div className="shipping-data">
                <h3>Delivery address</h3>
                <div className="input-inline">
                  <div className="input-group firstname">
                    <label htmlFor="firstname">First name</label>
                    <input
                      value={firstname}
                      onChange={(e) => setFirstname(e.target.value)}
                      type="text"
                    />
                  </div>
                  <div className="input-group lastname">
                    <label htmlFor="lastname">Last name</label>
                    <input
                      type="text"
                      value={lastname}
                      onChange={(e) => setLastname(e.target.value)}
                    />
                  </div>
                </div>
                <div className="input-group address">
                  <label htmlFor="address">Address</label>
                  <input
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    type="text"
                  />
                </div>
                <div className="input-group apartment">
                  <label htmlFor="apartment">Apartment, suite, etc.</label>
                  <input
                    value={apartment}
                    onChange={(e) => setApartment(e.target.value)}
                    type="text"
                  />
                </div>
                <div className="input-group city">
                  <label htmlFor="city">City</label>
                  <input
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    type="text"
                  />
                </div>
                <div className="input-inline">
                  <div className="input-group firstname">
                    <label htmlFor="firstname">Country / Region</label>
                    <select
                      onChange={(e) => setCountry(e.target.value)}
                      loading={countries.length < 1}>
                      {countries &&
                        countries.map((item) => (
                          <option key={item.k} value={item.value}>
                            {item.text}
                          </option>
                        ))}
                    </select>
                  </div>
                  <div className="input-group postalcode">
                    <label htmlFor="postalcode">Postal code</label>
                    <input
                      value={postalCode}
                      onChange={(e) => setPostalCode(e.target.value)}
                      type="text"
                    />
                  </div>
                </div>
                <div className="input-default">
                  <input
                    type="checkbox"
                    checked={makeDefault}
                    onChange={() => setMakeDefault(!makeDefault)}
                  />
                  <span>Make this default address?</span>
                </div>
              </div>
              <div className="checkout-nav">
                <Link to="/order-summary">
                  <i className="fa fa-chevron-left" />
                  Return to cart
                </Link>
                {cartItems?.length > 0 && (
                  <Button
                    loading={saving}
                    disabled={
                      saving ||
                      !contact ||
                      !firstname ||
                      !lastname ||
                      !address ||
                      !apartment ||
                      !city ||
                      !postalCode
                    }
                    type="submit"
                    className="checkbox-btn">
                    Continue to shipping
                  </Button>
                )}
              </div>
            </form>
          </div>
          <div className="checkout-summary hide-mobile">
            <h3 className="checkout-title">Order summary</h3>
            {cartItems?.length === 0 && (
              <>
                <div className="checkout-summary__empty">
                  <p>Your cart is empty</p>
                  <Link to="/all-products">Continue Shopping</Link>
                </div>
              </>
            )}
            {cartItems?.length > 0 && (
              <>
                <div
                  className={
                    cart?.order_items.length > 3
                      ? "checkout-orders scroll"
                      : "checkout-orders"
                  }>
                  {cartItems &&
                    cartItems.map((item) => (
                      <div className="checkout-order">
                        <div className="checkout-order__img">
                          <img
                            src={getImage(item.item_obj.image)}
                            alt={item?.item}
                          />
                          <span>{item?.quantity}</span>
                        </div>
                        <div className="checkout-order__detail">
                          <h3>{item?.item}</h3>
                          <p className="text-muted">
                            {item?.item_obj.weight} / {item?.item_obj.category}{" "}
                            / Peppermint
                          </p>
                        </div>
                        <h3>KSh. {item?.final_price.toFixed(2)}</h3>
                      </div>
                    ))}
                </div>
                <div className="underline" />
                <div className="checkout-subtotal">
                  <div>
                    <h3 className="text-muted">Subtotal</h3>
                    <h3 className="checkout-subtotal__price">
                      KSh. {cart?.total.toFixed(2)}
                    </h3>
                  </div>
                  <div>
                    <h3 className="text-muted">Shipping</h3>
                    <p className="text-muted">Calculated at payment step</p>
                  </div>
                </div>
                <div className="underline" />
                <div className="checkout-total">
                  <h3>Total</h3>
                  <h2>KSh. {cart?.total.toFixed(2)}</h2>
                </div>
              </>
            )}
          </div>
        </div>
      </Container>
    </>
  );
};

const mapStateToProps = (state) => ({
  cart: state.cart.shoppingCart,
});

export default connect(mapStateToProps, { fetchCart, addAddress })(Checkout);
