import React, { useState } from "react";
import { IoMdClose } from "react-icons/io";
import { addAddress } from "../../redux/action/address";
import "./style.css";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { Button } from "semantic-ui-react";

const AddAddress = () => {
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [contact, setContact] = useState("");
  const [address, setAddress] = useState("");
  const [apartment, setApartment] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [city, setCity] = useState("");
  const [country, setCountry] = useState("");
  const [userID, setUserID] = useState(null);
  const [countries, setCountries] = useState([]);
  const [makeDefault, setMakeDefault] = useState(false);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [details, setDetails] = useState(null);
  const [update, setUpdate] = useState(false);

  const handleSubmitAddress = (e) => {
    e.preventDefault();
    const data = {
      contact,
      first_name: firstname,
      last_name: lastname,
      address,
      apartment,
      city,
      country_or_region: country,
      postal_code: postalCode,
      default: makeDefault,
    };
    addAddress(data);
  };

  const closeModal = () => {
    document.querySelector(".modal-overlay").classList.add("hide");
    document.querySelector(".add-address").classList.add("hide");
  };

  if (success) {
    closeModal();
  }

  window.addEventListener("click", (e) => {
    if (e.target.classList.contains("modal-overlay")) {
      document.querySelector(".modal-overlay").classList.add("hide");
      document.querySelector(".add-address").classList.add("hide");
    }
  });

  return (
    <div className="modal-overlay hide add-address">
      <div className="modal-wrapper">
        <div className="modal-header">
          <span className="modal-title">Add New Address</span>
          <div className="modal-title__btn">
            <IoMdClose onClick={() => closeModal()} />
          </div>
        </div>
        <div className="underline" />
        <form
          className="shipping-form"
          onSubmit={(e) => handleSubmitAddress(e)}>
          <div className="shipping-contact">
            <h3>Contact information</h3>
            <div>
              <label htmlFor="contact">Mobile number</label>
              <div className="input-group phone">
                <div className="phone_code">+254</div>
                <input
                  placeholder="E.g 79000000"
                  value={contact}
                  onChange={(e) => setContact(e.target.value)}
                  type="text"
                />
              </div>
            </div>
          </div>
          <div className="shipping-data">
            <h3>Delivery address</h3>
            <div className="input-inline">
              <div className="input-group firstname">
                <label htmlFor="firstname">First name</label>
                <input
                  value={firstname}
                  onChange={(e) => setFirstname(e.target.value)}
                  type="text"
                />
              </div>
              <div className="input-group lastname">
                <label htmlFor="lastname">Last name</label>
                <input
                  type="text"
                  value={lastname}
                  onChange={(e) => setLastname(e.target.value)}
                />
              </div>
            </div>
            <div className="input-group firstname">
              <label htmlFor="firstname">First name</label>
              <input
                value={firstname}
                onChange={(e) => setFirstname(e.target.value)}
                type="text"
              />
            </div>
            <div className="input-group address">
              <label htmlFor="address">Address</label>
              <textarea
                value={address}
                onChange={(e) => setAddress(e.target.value)}
                type="text"
              />
            </div>
            <div className="input-group apartment">
              <label htmlFor="apartment">Apartment, suite, etc.</label>
              <input
                value={apartment}
                onChange={(e) => setApartment(e.target.value)}
                type="text"
              />
            </div>
            <div className="input-group city">
              <label htmlFor="city">City</label>
              <input
                value={city}
                onChange={(e) => setCity(e.target.value)}
                type="text"
              />
            </div>
            <div className="input-inline">
              <div className="input-group firstname">
                <label htmlFor="firstname">Country / Region</label>
                <select
                  onChange={(e) => setCountry(e.target.value)}
                  loading={countries.length < 1}>
                  {countries &&
                    countries.map((item) => (
                      <option key={item.k} value={item.value}>
                        {item.text}
                      </option>
                    ))}
                </select>
              </div>
              <div className="input-group postalcode">
                <label htmlFor="postalcode">Postal code</label>
                <input
                  value={postalCode}
                  onChange={(e) => setPostalCode(e.target.value)}
                  type="text"
                />
              </div>
            </div>
            <div className="input-default">
              <input
                type="checkbox"
                checked={makeDefault === true ? true : false}
                onChange={() => setMakeDefault(!makeDefault)}
              />
              <span>Make this default address?</span>
            </div>
          </div>
          <div className="checkout-nav">
            <Link to="/order-summary">
              <i className="fa fa-chevron-left" />
              Return to cart
            </Link>
            <Button
              loading={saving}
              disabled={
                saving ||
                !contact ||
                !firstname ||
                !lastname ||
                !address ||
                !apartment ||
                !city ||
                !postalCode
              }
              type="submit"
              className="checkbox-btn">
              Continue to shipping
            </Button>
          </div>
        </form>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => ({
  loading: state.address.loading,
  success: state.address.success,
});

export default connect(mapStateToProps, { addAddress })(AddAddress);
