import React, { useEffect } from "react";
import { FiChevronDown } from "react-icons/fi";
import {
  RiShoppingBagLine,
  RiUserLine,
  RiSearch2Line,
  RiLoginBoxLine,
  RiHome2Line,
  RiHeartLine,
} from "react-icons/ri";
import { Link, useLocation } from "react-router-dom";
import logo from "../../assets/imgs/logo.jpg";
import { connect } from "react-redux";
import { fetchCart } from "../../redux/action/cart";
import "./style.css";
import { Container } from "semantic-ui-react";

const Nav = ({ fetchCart, cart }) => {
  useEffect(() => {
    fetchCart();
  }, []);

  let cartItems = cart?.order_items;

  const { pathname } = useLocation();

  localStorage.setItem("cart", JSON.stringify(cartItems));

  // useEffect(() => {
  //   const stickyEl = document.querySelector(".sticky");
  //   const stickyPos = stickyEl.getBoundingClientRect().top;
  //   const offset = -20;

  //   console.log(stickyEl);

  //   window.addEventListener("scroll", () => {
  //     if (window.pageXOffset >= stickyPos + offset) {
  //       stickyEl.style.position = "fixed";
  //       stickyEl.style.top = "0px";
  //     } else {
  //       stickyEl.style.position = "static";
  //       stickyEl.style.top = "";
  //     }
  //   });
  // }, []);

  const stickyEl = document.querySelector(".sticky");

  const offset = -20;

  window.addEventListener("scroll", () => {
    // if (stickyEl) {
    //   const stickyPos = stickyEl.getBoundingClientRect().top;
    //   console.log("window.pageYOffset", window.pageYOffset);
    //   console.log("stickyPos", stickyPos);
    //   if (stickyPos <= 0) {
    //     stickyEl.classList.add("fixed");
    //   }
    //   if (window.pageYOffset <= 240) {
    //     stickyEl.classList.remove("fixed");
    //     stickyEl.style.position = "static";
    //     stickyEl.style.top = "";
    //   }
    // }
  });

  const authLinks = (
    <div className="auth-links">
      <Link to="/profile">
        <RiUserLine />
      </Link>
      <Link to="/wish-list">
        <RiHeartLine />
      </Link>
      <Link to="/wish-list">
        <RiShoppingBagLine />
      </Link>
    </div>
  );
  const guestLinks = <div className="guest-links"></div>;

  const navLogo = (
    <div className="nav-logo">
      <Link to="/">
        <img src={logo} alt="LuxurySilk" />
      </Link>
    </div>
  );

  const navMid = (
    <div className="nav-main">
      <ul className="menu">
        <li>
          <Link to="/">Home</Link>
        </li>
        <li className="dropdown">
          <Link className="dropbtn" to="/collections/all-products">
            Shop
            <FiChevronDown />
          </Link>
          <div class="dropdown-content">
            <Link to="/collections/shampoo">Shampoo</Link>
            <Link to="/collections/hair-oil">Hair Oil</Link>
            <Link to="/collections/moisturizer">Moisturizer</Link>
          </div>
        </li>
        <li>
          <Link to="/about-us">About us</Link>
        </li>
        <li>
          <Link to="/contact-us">Contact us</Link>
        </li>
      </ul>
    </div>
  );

  const navLinks = <div className="nav-links">{authLinks}</div>;

  return (
    <>
      {pathname !== "/payment" &&
        pathname !== "/login" &&
        pathname !== "/register" && (
          <div id="nav" className="nav">
            <Container className="nav-wrapper">
              {navLogo}
              {navMid}
              {navLinks}
            </Container>
          </div>
        )}
      {pathname === "/payment" && (
        <div className="nav-payment">
          <Container>
            <Link to="/">
              <h1>
                luxury
                <span>
                  silk
                  <i className="fa fa-shield" />
                  pay
                </span>
              </h1>
            </Link>
          </Container>
        </div>
      )}
    </>
  );
};

const mapStateToProps = (state) => ({
  cart: state.cart.shoppingCart,
});

export default connect(mapStateToProps, { fetchCart })(Nav);
