import React, { useEffect } from "react";
import { connect } from "react-redux";
import { Button, Container } from "semantic-ui-react";
import { fetchCart } from "../redux/action/cart";
import { Link } from "react-router-dom";
import { SlideData } from "../data/data";
import Hero from "./Hero";
import Featured from "./Featured";
import img from "../assets/imgs/hero2.jpg";
import oil from "../assets/imgs/oil-home.jpg";

import shampoo1 from "../assets/imgs/shampoo1.jpg";
import shampoo2 from "../assets/imgs/shampoo2.jpg";
import shampoo3 from "../assets/imgs/shampoo3.jpg";

const Home = ({ products, fetchCart }) => {
  useEffect(() => {
    fetchCart();
  }, []);

  let items = products?.results;

  let shampoo = items
    ?.filter((item) => item.category === "Shampoo")
    .slice(0, 4);

  let oil = items?.filter((item) => item.category === "Hair Oil").slice(0, 4);

  let moisturizer = items
    ?.filter((item) => item.category === "Moisturizer")
    .slice(0, 4);

  console.log(shampoo);

  const showQuickView = () => {
    document.querySelector(".product-modal").classList.add("transparentBcg");
    document.querySelector(".product-modal__wrapper").classList.add("show");
  };

  document.title = "Home | Luxury Silk Online";

  return (
    <>
      <div className="homepage">
        <section className="showcase">
          <img src={img} alt="" />
        </section>
        <div className="underline" />
        <section className="products">
          <Container className="products-wrapper">
            <h1 className="home-title">Shampoo</h1>
            <div className="products-items">
              {shampoo?.length > 0 &&
                shampoo.map((item) => (
                  <div className="product-item">
                    <img className="product-item-img" src={item.image} alt="" />
                    <div className="product-item-info">
                      <p className="product-name">Product Name</p>
                      <h5 className="product-price">Ksh. 1, 060</h5>
                    </div>
                    <div
                      onClick={() => showQuickView()}
                      className="product-quickview">
                      <p>quick vieww</p>
                    </div>
                  </div>
                ))}
            </div>
          </Container>
        </section>
        <section className="products">
          <Container className="products-wrapper">
            <h1 className="home-title">Hair Oil</h1>
            <div className="products-items">
              {oil?.length > 0 &&
                oil.map((item) => (
                  <div className="product-item">
                    <img className="product-item-img" src={item.image} alt="" />
                    <div className="product-item-info">
                      <p className="product-name">Product Name</p>
                      <h5 className="product-price">Ksh. 1, 060</h5>
                    </div>
                    <div
                      onClick={() => showQuickView()}
                      className="product-quickview">
                      <p>quick vieww</p>
                    </div>
                  </div>
                ))}
            </div>
          </Container>
        </section>
        <section className="oil-product">
          <h1 className="home-title">Hair Oil</h1>
          <Container className="oil__wrapper">
            <div className="oil__wrapper-img">
              <img src={oil} alt="oil" />
            </div>
            <div className="oil__wrapper-info">
              <div className="product-modal__info">
                <h3>Product title</h3>
                <h4>Ksh 1600</h4>
                <div className="underline" />
                <div className="product-add">
                  <h5>Quantity</h5>
                  <div className="qty-selection">
                    <a field="quantity" className="down quantity-control-down">
                      -
                    </a>
                    <input
                      type="text"
                      min="1"
                      name="quantity"
                      value="1"
                      className="quantity"
                    />
                    <a field="quantity" className="up quantity-control-up">
                      +
                    </a>
                  </div>
                </div>
                <div className="btn-group">
                  <Button className="btn-add">Add to Cart</Button>
                  <Button className="btn-buy">Buy it now</Button>
                </div>
              </div>
            </div>
          </Container>
        </section>
        <section className="products">
          <Container className="products-wrapper">
            <h1 className="home-title">Moisturizer</h1>
            <div className="products-items">
              {moisturizer?.length > 0 &&
                moisturizer.map((item) => (
                  <div className="product-item">
                    <img className="product-item-img" src={item.image} alt="" />
                    <div className="product-item-info">
                      <p className="product-name">Product Name</p>
                      <h5 className="product-price">Ksh. 1, 060</h5>
                    </div>
                    <div
                      onClick={() => showQuickView()}
                      className="product-quickview">
                      <p>quick vieww</p>
                    </div>
                  </div>
                ))}
            </div>
          </Container>
        </section>
      </div>
    </>
  );
};

const mapStateToProps = (state) => ({
  products: state.products.products,
});

export default connect(mapStateToProps, { fetchCart })(Home);
