import React, { useState } from "react";
import { Button } from "semantic-ui-react";

const EditDetails = () => {
  const [userName, setUserName] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");

  return (
    <form className="account_inner-content__details">
      <div className="input-group firstname">
        <label htmlFor="firstname">Username</label>
        <input
          value={userName}
          onChange={(e) => setUserName(e.target.value)}
          type="text"
        />
      </div>
      <div className="input-inline">
        <div className="input-group firstname">
          <label htmlFor="firstname">First name</label>
          <input
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
            type="text"
          />
        </div>
        <div className="input-group lastname">
          <label htmlFor="lastname">Last name</label>
          <input
            type="text"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
          />
        </div>
      </div>
      <div className="input-inline">
        <div className="input-group firstname">
          <label htmlFor="firstname">Email</label>
          <input
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            type="text"
          />
        </div>
        <div className="input-group lastname">
          <label htmlFor="lastname">Prefix</label>
          <div className="input-group phone">
            <div className="phone_code">+254</div>
            <input
              placeholder="E.g 79000000"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              maxLength="9"
              type="text"
            />
          </div>
        </div>
      </div>
      <Button type="submit">save</Button>
    </form>
  );
};

export default EditDetails;
