import React, { useEffect } from "react";
import { FiChevronDown, FiUser, FiShoppingCart } from "react-icons/fi";
import { RiHeartLine } from "react-icons/ri";
import { Link, useLocation } from "react-router-dom";
import logo from "../../assets/imgs/logo.jpg";
import { connect } from "react-redux";
import { fetchCart } from "../../redux/action/cart";
import "./style.css";
import { Container } from "semantic-ui-react";

const Nav = ({ fetchCart, cart, auth }) => {
  useEffect(() => {
    fetchCart();
  }, []);

  let cartItems = cart?.order_items;

  const { pathname } = useLocation();

  localStorage.setItem("cart", JSON.stringify(cartItems));

  // useEffect(() => {
  //   const stickyEl = document.querySelector(".sticky");
  //   const stickyPos = stickyEl.getBoundingClientRect().top;
  //   const offset = -20;

  //   console.log(stickyEl);

  //   window.addEventListener("scroll", () => {
  //     if (window.pageXOffset >= stickyPos + offset) {
  //       stickyEl.style.position = "fixed";
  //       stickyEl.style.top = "0px";
  //     } else {
  //       stickyEl.style.position = "static";
  //       stickyEl.style.top = "";
  //     }
  //   });
  // }, []);

  const stickyEl = document.querySelector(".sticky");

  const offset = -20;

  window.addEventListener("scroll", () => {
    // if (stickyEl) {
    //   const stickyPos = stickyEl.getBoundingClientRect().top;
    //   console.log("window.pageYOffset", window.pageYOffset);
    //   console.log("stickyPos", stickyPos);
    //   if (stickyPos <= 0) {
    //     stickyEl.classList.add("fixed");
    //   }
    //   if (window.pageYOffset <= 240) {
    //     stickyEl.classList.remove("fixed");
    //     stickyEl.style.position = "static";
    //     stickyEl.style.top = "";
    //   }
    // }
  });

  const authLinks = (
    <div className="auth-links">
      <Link to="/profile">
        <FiUser />
      </Link>
      <Link to="/wish-list">
        <RiHeartLine />
      </Link>
      <Link to="/wish-list">
        <FiShoppingCart />
      </Link>
    </div>
  );

  return (
    <>
      {pathname !== "/payment" &&
        pathname !== "/login" &&
        pathname !== "/register" && (
          <div id="nav" className="nav">
            <div className="nav-top">
              <div className="nav-socials">
                <Link to="">
                  <i className="fa fa-facebook-f" />
                </Link>
                <Link to="">
                  <i className="fa fa-instagram" />
                </Link>
                <Link to="">
                  <i className="fa fa-twitter" />
                </Link>
              </div>
              <div className="nav-info">
                <p>
                  For all orders made, the standard delivery timeline is between
                  <br />
                  24-72 hrs.
                </p>
              </div>
              <div className="nav-links">
                <Link to="/">
                  <i className="fa fa-search" />
                </Link>
                <Link to="/">
                  <i className="fa fa-home" />
                </Link>
                {auth?.isAuthenticated && localStorage.getItem("token") ? (
                  <>
                    <Link to="/profile">
                      <i className="fa fa-user" />
                    </Link>
                    <Link className="nav-cart" to="/order-summary">
                      my cart
                      <i className="fa fa-shopping-bag" />
                      <span>{cartItems?.length}</span>
                    </Link>
                  </>
                ) : (
                  <>
                    <Link className="nav-user" to="/login">
                      login
                      <i className="fa fa-sign-in" />
                    </Link>
                  </>
                )}
              </div>
            </div>
            <div className="nav-mid">
              <div className="nav-logo">
                <img src={logo} alt="Luxury Silk" />
              </div>
            </div>{" "}
            <div className="nav-main">
              <ul className="menu">
                <li>
                  <Link to="/">Home</Link>
                </li>
                <li className="dropdown">
                  <Link className="dropbtn" to="/collections/all-products">
                    Shop
                    <FiChevronDown />
                  </Link>
                  <div class="dropdown-content">
                    <Link to="/collections/shampoo">Shampoo</Link>
                    <Link to="/collections/hair-oil">Hair Oil</Link>
                    <Link to="/collections/moisturizer">Moisturizer</Link>
                  </div>
                </li>
                <li>
                  <Link to="/about-us">About us</Link>
                </li>
                <li>
                  <Link to="/contact-us">Contact us</Link>
                </li>
              </ul>
            </div>
          </div>
        )}
      {pathname === "/payment" && (
        <div className="nav-payment">
          <Container>
            <Link to="/">
              <h1>
                luxury
                <span>
                  silk
                  <i className="fa fa-shield" />
                  pay
                </span>
              </h1>
            </Link>
          </Container>
        </div>
      )}
    </>
  );
};

const mapStateToProps = (state) => ({
  cart: state.cart.shoppingCart,
  auth: state.auth,
});

export default connect(mapStateToProps, { fetchCart })(Nav);
