import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { Button, Container } from "semantic-ui-react";
import { FiMinus, FiPlus } from "react-icons/fi";
import { FaChevronLeft } from "react-icons/fa";
import { FiChevronDown } from "react-icons/fi";
import {
  getItemDetail,
  handleAddToCart,
  getItems,
} from "../redux/action/products";
import { connect } from "react-redux";
import img1 from "../assets/imgs/shampoo1.jpg";
import img2 from "../assets/imgs/shampoo2.jpg";
import img3 from "../assets/imgs/shampoo3.jpg";
import img4 from "../assets/imgs/shampoo4.jpg";
import { getImage } from "../utils";

const ProductDetail = ({
  getItemDetail,
  handleAddToCart,
  auth,
  loading,
  products,
  getItems,
  product: { item, error },
}) => {
  const [quantity, setQuantity] = useState(1);

  const { location } = useHistory();
  let slug = location.pathname;

  let itemSlug = `/products/${slug.split("/").slice(3, 4).toString()}`;

  useEffect(() => {
    getItemDetail(itemSlug);
  }, [itemSlug]);

  useEffect(() => {
    getItems();
  }, []);

  document.title = item
    ? `${item?.title} | Luxury Silk Online`
    : "Luxury Silk Online";

  const history = useHistory();

  const checkUser = (ref_code, qty) => {
    if (auth.token === null) {
      return history.push("/login");
    } else {
      return handleAddToCart(ref_code, qty);
    }
  };
  const category = item?.category;

  const items = products?.results;

  let similars = items
    ?.filter((item) => item.category === category)
    .slice(0, 4);
  console.log(similars);

  const showStepBtn = document.querySelectorAll(".show-step");
  showStepBtn.forEach((btn) => {
    btn.addEventListener("click", (e) => {
      document.querySelectorAll(".step").forEach((step) => {
        step.classList.remove("show-text");
      });
      e.currentTarget.parentElement.parentElement.classList.toggle("show-text");
    });
  });

  return (
    <>
      <div className="title">
        <p className="title-nav">
          <Link to="/">Home</Link>/<Link to="/collections">Collections</Link>/
          <Link to="/collections/all-products">All products</Link>/
          <span>{item.title}</span>
        </p>
        <h2 className="title-name">{item.title}</h2>
      </div>
      <Container>
        <div className="product">
          <div className="product_info">
            <div className="product_info-left">
              <h1 className="product_info-left__title">{item.title}</h1>
              <p className="product_info-left__desc">{item.description}</p>
              <div className="product_info-left__safe">
                <p>
                  <span>Safe for:</span> Normal, Dry, Combination, Sensitive
                </p>
              </div>
              <div className="product_info-left__use">
                <p>
                  <span>When to use:</span> Day or Night
                </p>
              </div>
            </div>
            <div className="product_info-right">
              <div className="product_info-right__img">
                <img src={getImage(item.image)} alt={item.title} />
              </div>
              <div className="product_info-right__info">
                <div className="product-purchase">
                  <h1 className="product_info-right__price">
                    KSh. {quantity * item.price}
                  </h1>
                  <div className="product_info-right__category">
                    {item.category}
                  </div>
                  <span className="product_info-right__weight">
                    {item?.weight}
                  </span>
                  <div className="product_info-right__add">
                    <div className="product_info-right__qty">
                      <FiMinus
                        className="qty-down down"
                        onClick={() => {
                          quantity > 1
                            ? setQuantity(quantity - 1)
                            : setQuantity(1);
                        }}
                      />
                      <span className="product_info-right__value">
                        {quantity}
                      </span>
                      <FiPlus
                        className="qty-up up"
                        onClick={() => setQuantity(quantity + 1)}
                      />
                    </div>
                    {item.sold_out ? (
                      <Button disabled className="add-btn">
                        sold out
                      </Button>
                    ) : (
                      <Button
                        loading={loading}
                        disabled={loading}
                        onClick={() => checkUser(item.ref_code, quantity)}
                        className="add-btn">
                        add to cart
                      </Button>
                    )}
                  </div>
                </div>
                <div className="product-extra">
                  <article className="step">
                    <div className="step-header">
                      <h4>direction of use</h4>
                      <FiChevronDown className="show-step" />
                    </div>
                    <div className="step-text">
                      <p>
                        On damp hair, separate into sections depending on volume
                        and length. If hair is dry, spritz lightly with our
                        "Miracle Mist" Daily Spritz to soften and dampen. Apply
                        "Curl Magic!" Curling Gel liberally sections by section
                        from root to tip.
                      </p>
                    </div>
                  </article>
                  <article className="step">
                    <div className="step-header">
                      <h4>Ingredients</h4>
                      <FiChevronDown className="show-step" />
                    </div>
                    <div className="step-text">
                      <p>
                        Deionized Water, Glycerin, Carbomer Solution, Provitamin
                        B5, Flax Seed Gel, Aloe Vera Gel, Sunflower Oil,
                        Peppermint Leaves, Phenoxyethanol, Berries & Fruit
                        Extract.
                      </p>
                    </div>
                  </article>
                </div>
              </div>
            </div>
          </div>

          <div className="product_desc">
            <h3 className="product_desc-title">Description</h3>
            <div className="underline" />
            <div className="product_desc-item">
              <h4>directions of use:</h4>
              <p>
                On damp hair, separate into sections depending on volume and
                length. If hair is dry, spritz lightly with our{" "}
                <strong>"Miracle Mist" Daily Spritz</strong> to soften and
                dampen. Apply <strong>"Curl Magic!" Curling Gel </strong>
                liberally sections by section from root to tip. Curls will
                appear instantly! Let hair air-dry. Perfect for wash & go's, the
                curling sponge, rod-sets, finger coils, Bantu knots, twists,
                braids, and all other styles! If twist-outs or braid-outs, take
                down when completely dry for stronger definition.
              </p>
            </div>
            <div className="product_desc-item">
              <h4>unisex & great for all!</h4>
              <p>
                Women. Men. Kids. Naturals and Trasitioners. (This product is
                not suitable for chemically treated or relaxed hair.)
              </p>
            </div>
            <div className="product_desc-item">
              <h4>Ingredients:</h4>
              <p>
                Deionized Water, Glycerin, Carbomer Solution, Provitamin B5,
                Flax Seed Gel, Aloe Vera Gel, Sunflower Oil, Peppermint Leaves,
                Phenoxyethanol, Berries & Fruit Extract.
              </p>
            </div>
          </div>
          <div className="product_related">
            <h2>you may also like</h2>
            <div className="underline" />
            <div className="product_related-items">
              {similars &&
                similars.map((item) => (
                  <div className="product_related-item">
                    <Link to="">
                      <img src={item.image} alt="" />
                    </Link>
                    <div>
                      <Link to="">
                        <h3>{item.title}</h3>
                      </Link>
                      <h4>KSH {item.price}</h4>
                    </div>
                  </div>
                ))}
            </div>
            <Link to="/products" className="product_related-back">
              <FaChevronLeft /> Back to PRODUCTS
            </Link>
          </div>
        </div>
      </Container>
    </>
  );
};

const maptStateToProps = (state) => ({
  product: state.product,
  auth: state.auth,
  products: state.products.products,
  loading: state.cart.loading,
});

export default connect(maptStateToProps, {
  handleAddToCart,
  getItemDetail,
  getItems,
})(ProductDetail);
