import React, { useEffect, useState } from "react";
import { Button } from "semantic-ui-react";
import { connect } from "react-redux";
import { editUserURL } from "../constant";
import { authAxios, config } from "../utils";

const EditDetails = ({ user, profile }) => {
  const [userName, setUserName] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");

  useEffect(() => {
    setUserName(user?.username);
    setEmail(user?.email);
    setFirstName(profile?.first_name);
    setLastName(profile?.last_name);
    setPhone(profile?.phone_number);
  }, [user, profile]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const data = JSON.stringify({
      user_name: userName,
      first_name: firstName,
      last_name: lastName,
      email,
      phone,
    });
    console.log(data);
    updateUser(data);
  };

  const updateUser = (data) => {
    authAxios
      .put(editUserURL, data, config)
      .then((res) => {
        console.log(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <form
      onSubmit={(e) => handleSubmit(e)}
      className="account_inner-content__details">
      <div className="input-group firstname">
        <label htmlFor="firstname">Username</label>
        <input
          value={userName}
          onChange={(e) => setUserName(e.target.value)}
          type="text"
        />
      </div>
      <div className="input-inline">
        <div className="input-group firstname">
          <label htmlFor="firstname">First name</label>
          <input
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
            type="text"
          />
        </div>
        <div className="input-group lastname">
          <label htmlFor="lastname">Last name</label>
          <input
            type="text"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
          />
        </div>
      </div>
      <div className="input-inline">
        <div className="input-group firstname">
          <label htmlFor="firstname">Email</label>
          <input
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            type="text"
          />
        </div>
        <div className="input-group lastname">
          <label htmlFor="lastname">Prefix</label>
          <div className="input-group phone">
            <div className="phone_code">+254</div>
            <input
              placeholder="E.g 79000000"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              maxLength="9"
              type="text"
            />
          </div>
        </div>
      </div>
      <Button type="submit">save</Button>
    </form>
  );
};

const mapStateToProps = (state) => ({
  user: state.auth.user,
  profile: state.auth.profile,
});

export default connect(mapStateToProps)(EditDetails);
