import { authAxios, tokenConfig } from "../../utils";
import { CART_START, CART_SUCCESS, CART_FAIL } from "../types/index";
import { orderSummaryURL } from "../../constant";

export const fetchCart = () => (dispatch, getState) => {
  dispatch({
    type: CART_START,
  });
  authAxios
    .get(orderSummaryURL, tokenConfig(getState))
    .then((res) => {
      localStorage.setItem("cart", res.data);
      dispatch({
        type: CART_SUCCESS,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: CART_FAIL,
        payload: err,
      });
    });
};
