import {
  SAVE_ADDRESS_SUCCESS,
  SAVE_ADDRESS_ERROR,
  FETCH_ADDRESS_LOADING,
  FETCH_ADDRESS_SUCCESS,
  FETCH_ADDRESS_ERROR,
} from "../types";

const initialState = {
  loading: false,
  address: null,
  error: null,
};

export default function (state = initialState, { type, payload }) {
  switch (type) {
    case FETCH_ADDRESS_LOADING:
      return {
        ...state,
        loading: true,
      };
    case FETCH_ADDRESS_SUCCESS:
      return {
        ...state,
        loading: false,
        address: payload,
      };
    case FETCH_ADDRESS_ERROR:
      return {
        ...state,
        loading: false,
        error: payload,
      };
    default:
      return state;
  }
}
