import React, { useEffect, useState } from "react";
import { FaCartPlus, FaStar, FaStarHalfAlt } from "react-icons/fa";
import { IoMdArrowForward } from "react-icons/io";
import { Link, useHistory } from "react-router-dom";
import { Button, Container } from "semantic-ui-react";
// import products from "../data/data";
import { getItems, handleAddToCart } from "../redux/action/products";
import { fetchCart } from "../redux/action/cart";
import { connect } from "react-redux";
import axios from "axios";
import { productListURL } from "../constant";

const Products = ({
  handleAddToCart,
  fetchCart,
  products,
  loading,
  getItems,
  auth,
}) => {
  const [list, setList] = useState(false);
  const [price, setPrice] = useState("200");

  useEffect(() => {
    getItems();
    fetchCart();
  }, []);

  const categories = [
    {
      id: "all",
      value: "All",
      active: true,
    },
    {
      id: "shampoo",
      value: "Shampoo",
      active: false,
    },
    {
      id: "moisturizer",
      value: "Moisturizer",
      active: false,
    },
    {
      id: "hair oil",
      value: "Hair Oil",
      active: false,
    },
  ];

  const getCategory = (e) => {
    let shampoo = products?.results.filter(
      (item) => item.category === e.target.value
    );
    console.log(e.target.value);
    console.log("Category", shampoo);
  };

  // getCategory();

  const getSlug = (slug) => {
    return `/products/${slug}`;
  };

  document.title = "Products | Luxury Silk Online";

  const history = useHistory();

  const checkUser = (code) => {
    if (auth.token === null) {
      return history.push("login");
    } else {
      return handleAddToCart(code);
    }
  };

  const filterPerc = () => {
    let tempArr = products?.results.map((item) =>
      Math.abs(Math.floor(item.perc_off))
    );
    const pricesArr = tempArr;
  };

  filterPerc();

  const filterPrice = (perc) => {
    products.map((item) => {
      if (item.discount_price && item.discount_price === perc) {
        console.log(item);
      } else if (!item.discount_price && item.price === perc) {
        console.log(item);
      }
    });
  };

  return (
    <>
      <Container className="user-nav">
        <p>
          <Link to="/">Home</Link> > All Products
        </p>
      </Container>
      <Container className="products">
        <div className="products__inner">
          <div className="products-filter hide-mobile">
            <div className="products-filter__group">
              <h4>category</h4>
              <ul>
                <li
                  value="All"
                  onClick={getCategory}
                  className="products-filter__item active">
                  All
                </li>
                <li
                  value="Shampoo"
                  onClick={() => getCategory()}
                  className="products-filter__item">
                  Shampoo
                </li>
                <li
                  value="Moisturizer"
                  onClick={() => getCategory()}
                  className="products-filter__item">
                  Moisturizer
                </li>
                <li
                  value="Hair Oil"
                  onClick={() => getCategory()}
                  className="products-filter__item">
                  Hair Oil
                </li>
              </ul>
            </div>
            <div className="underline" />
            <div className="products-filter__group">
              <h4>brand</h4>
              <div className="input-check">
                <input type="checkbox" />
                <label htmlFor="fashion">Fashion</label>
              </div>
              <div className="input-check">
                <input type="checkbox" />
                <label htmlFor="fashion">Generic</label>
              </div>
            </div>
            <div className="underline" />
            <div className="products-filter__group">
              <h4>weight</h4>
              <div className="input-check">
                <input type="checkbox" />
                <label htmlFor="fashion">500g</label>
              </div>
              <div className="input-check">
                <input type="checkbox" />
                <label htmlFor="fashion">750g</label>
              </div>
            </div>
            <div className="underline" />
            <div className="products-filter__group">
              <h4>
                price (ksh) <span className="text-muted">Ksh. {price}</span>
              </h4>
              <div className="input-check">
                <input
                  type="range"
                  max="3000"
                  min="200"
                  onChange={(e) => {
                    filterPrice(e.target.value);
                    setPrice(e.target.value);
                  }}
                />
              </div>
            </div>

            <div className="underline" />
            <div className="products-filter__group">
              <h4>Discount percentage</h4>
              <div className="input-check circle">
                <input type="checkbox" onChange={() => filterPerc(50)} />
                <label htmlFor="fashion">50% or more</label>
              </div>
              <div className="input-check circle">
                <input type="checkbox" onChange={() => filterPerc(40)} />
                <label htmlFor="fashion">40% or more</label>
              </div>
              <div className="input-check circle">
                <input type="checkbox" onChange={() => filterPerc(30)} />
                <label htmlFor="fashion">30% or more</label>
              </div>
              <div className="input-check circle">
                <input type="checkbox" onChange={() => filterPerc(20)} />
                <label htmlFor="fashion">20% or more</label>
              </div>
              <div className="input-check circle">
                <input type="checkbox" onChange={() => filterPerc(10)} />
                <label htmlFor="fashion">10% or more</label>
              </div>
            </div>
            <div className="underline" />
            <div className="products-filter__group">
              <h4>shipped from</h4>
              <ul>
                <li>Kenya</li>
              </ul>
            </div>
            <div className="underline" />
            <div className="products-filter__group">
              <h4>hair type</h4>
              <div className="input-check">
                <input type="checkbox" />
                <label htmlFor="fashion">All hair type</label>
              </div>
              <div className="input-check">
                <input type="checkbox" />
                <label htmlFor="fashion">Soft hair</label>
              </div>
              <div className="input-check">
                <input type="checkbox" />
                <label htmlFor="fashion">Rough hair</label>
              </div>
            </div>
          </div>
          <div className="products-content">
            <div className="products-sort">
              <p>
                <strong>Sort by: </strong>
                <select className="products_select">
                  <option>Popularity</option>
                  <option>Newest Arrivals</option>
                  <option>Price: Low to High</option>
                  <option>Price: High to Low</option>
                  <option>Product Rating</option>
                </select>
              </p>
            </div>
            <div className="underline" />
            <div className="products-tab">
              <p className="products-num text-muted">
                {products?.results.length > 0 && (
                  <>
                    {products?.results.length} product
                    {products?.results.length > 1 ? "s" : ""} found
                  </>
                )}
                {products?.results.length < 1 && <>No products found</>}
              </p>
              <div className="products-display">
                <i
                  onClick={() => {
                    document
                      .querySelector(".fa-th-list")
                      .classList.add("current");
                    document
                      .querySelector(".fa-th-large")
                      .classList.remove("current");
                    setList(true);
                  }}
                  className="fa fa-th-list"></i>
                <i
                  onClick={() => {
                    document
                      .querySelector(".fa-th-large")
                      .classList.add("current");
                    document
                      .querySelector(".fa-th-list")
                      .classList.remove("current");
                    setList(false);
                  }}
                  className="fa fa-th-large current"></i>
              </div>
            </div>
            <div className="underline" />
            {loading && <p>loading</p>}
            <div className={list ? "products-items list" : "products-items"}>
              {products?.results.length > 0 &&
                products?.results.map((product) => (
                  <div
                    className={
                      product.sold_out
                        ? list
                          ? "products-item sold list"
                          : "products-item sold"
                        : list
                        ? "products-item list"
                        : "products-item"
                    }
                    key={product.ref_code}>
                    <Link to={getSlug(product.slug)}>
                      <img
                        className="products-item__img"
                        src={product.image}
                        alt={product.title}
                      />
                    </Link>
                    <div
                      className={
                        list
                          ? "products-item__info list"
                          : "products-item__info"
                      }>
                      <span
                        className={
                          list
                            ? "products-item__store list"
                            : "products-item__store"
                        }>
                        {product.category}
                      </span>
                      <Link to={getSlug(product.slug)}>
                        <h4>{product.title}</h4>
                      </Link>
                      {product.discount_price ? (
                        <h3>KSh. {product.discount_price}</h3>
                      ) : (
                        <h3>KSh. {product.price}</h3>
                      )}
                      {product.discount_price && (
                        <div>
                          <h3>KSh. {product.price}</h3>
                          <span>{Math.floor(product.perc_off)}% off</span>
                        </div>
                      )}
                      {product.sold_out ? (
                        <button
                          disabled={product.sold_out}
                          onClick={() => checkUser()}
                          className={
                            list ? "btn-cart sold list" : "btn-cart sold"
                          }>
                          Sold Out
                        </button>
                      ) : (
                        <button
                          id="add-cart"
                          data-id={product.ref_code}
                          onClick={() => checkUser(product.ref_code)}
                          className={list ? "btn-cart list" : "btn-cart"}>
                          Add to cart <FaCartPlus />
                        </button>
                      )}
                    </div>
                  </div>
                ))}
            </div>
            <div className="products-pagination">
              <Button disabled={!products?.previous} className="paginate-btn">
                <i className="fa fa-angle-double-left" />
              </Button>
              <Button disabled={!products?.previous} className="paginate-btn">
                <i className="fa fa-angle-left" />
              </Button>
              <Button className="paginate-btn current">
                <span>1</span>
              </Button>
              {products?.next && (
                <Button disabled={!products?.next} className="paginate-btn">
                  <span>2</span>
                </Button>
              )}
              {products?.next && (
                <Button disabled={!products?.next} className="paginate-btn">
                  <span>3</span>
                </Button>
              )}

              <Button disabled={!products?.next} className="paginate-btn">
                <i className="fa fa-angle-right" />
              </Button>
              <Button disabled={!products?.next} className="paginate-btn">
                <i className="fa fa-angle-double-right" />
              </Button>
            </div>
          </div>
        </div>
      </Container>
    </>
  );
};

const mapStateToProps = (state) => ({
  products: state.products.products,
  loading: state.products.loading,
  auth: state.auth,
});

export default connect(mapStateToProps, {
  handleAddToCart,
  fetchCart,
  getItems,
})(Products);
