import { authAxios } from "../../utils";
import { saveAddressURL } from "../../constant";
import { SAVE_ADDRESS_SUCCESS, SAVE_ADDRESS_ERROR } from "../types";

export const saveAddress = ({
  firstName,
  lastName,
  phone,
  extra,
  address,
  state,
  city,
}) => (dispatch) => {
  const body = JSON.stringify({
    first_name: firstName,
    last_name: lastName,
    phone_number: phone,
    additional_info: extra,
    delivery_address: address,
    state_or_region: state,
    city: city,
  });

  const config = {
    headers: {
      "Content-Type": "application/json",
    },
  };

  authAxios
    .post(saveAddressURL, body, config)
    .then((res) => {
      dispatch({
        type: SAVE_ADDRESS_SUCCESS,
        payload: res.data,
      });

      console.log(res.data);
    })
    .catch((err) => {
      dispatch({
        type: SAVE_ADDRESS_ERROR,
        payload: err,
      });
      console.log(err);
    });
};
