import React, { useEffect } from "react";
import { Button, Container } from "semantic-ui-react";
import { fetchCart } from "../redux/action/cart";
import { Link, Redirect, useHistory } from "react-router-dom";
import { connect } from "react-redux";
import { getImage } from "../utils";

const Payment = ({ fetchCart, cart }) => {
  useEffect(() => {
    fetchCart();
  }, []);

  let cartItems = cart?.order_items;

  const history = useHistory();

  if (cartItems?.length < 1) {
    return history.goBack();
  }

  document.title = `Payment ( KSh. ${cart?.total}  ) | Luxury Silk Online`;
  return (
    <>
      <div className="payment">
        <div className="payment-info">
          <div className="payment-info__user">
            <div className="payment-info__item">
              <h4 className="text-muted">Contact</h4>
              <div>
                <h3>+254795772333</h3>
                <Link to="">Change</Link>
              </div>
            </div>
            <div className="payment-info__item">
              <h4 className="text-muted">Deliver to</h4>
              <div>
                <h3>144, 40600, Siaya, 144, Siaya</h3>
                <Link to="">Change</Link>
              </div>
            </div>
            <div className="payment-info__item">
              <h4 className="text-muted">Delivery cost</h4>
              <div>
                <h3>KSh. 350</h3>
              </div>
            </div>
          </div>
          <h3 className="checkout-title">Payment</h3>
          <p className="payment-det">
            All trasactions are secured and encrypted
          </p>
        </div>
      </div>
    </>
  );
};

const mapStateToProps = (state) => ({
  cart: state.cart.shoppingCart,
});

export default connect(mapStateToProps, { fetchCart })(Payment);
