import React, { useEffect, useState } from "react";
import { Button, Container } from "semantic-ui-react";
import { fetchCart } from "../redux/action/cart";
import { Link, useHistory } from "react-router-dom";
import { connect } from "react-redux";
import { getImage } from "../utils";
import { addAddress } from "../redux/action/address";

const Checkout = ({ fetchCart, cart, addAddress, loading }) => {
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [state, setState] = useState("");
  const [city, setCity] = useState("");

  useEffect(() => {
    fetchCart();
  }, []);

  let cartItems = cart?.order_items;
  const history = useHistory();

  const data = {
    firstname,
    lastname,
    phone,
    address,
    state,
    city,
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(data);
    addAddress(data);
  };

  if (cartItems?.length < 1) {
    return history.goBack();
  }

  document.title = `Checkout ( ${cart?.order_items.length} items ) | Luxury Silk Online`;
  return (
    <>
      <div className="title">
        <p className="title-nav">
          <Link to="/">Home</Link>/
          <Link to="/order-summary">Shopping Cart</Link>/<span>Checkout</span>
        </p>
        <h2 className="title-name">Checkout</h2>
      </div>
      <Container className="checkout">
        <div className="checkout__wrapper">
          <div className="checkout-address">
            <h3 className="checkout-title">Delivery address</h3>
            <form onSubmit={(e) => handleSubmit(e)}>
              <div className="input__inline">
                <div className="input-group">
                  <label htmlFor="firstName">First name</label>
                  <input
                    type="text"
                    value={firstname}
                    onChange={(e) => setFirstname(e.target.value)}
                    autoComplete="false"
                    required
                  />
                </div>
                <div className="input-group">
                  <label htmlFor="lastName">Last name</label>
                  <input
                    type="text"
                    value={lastname}
                    onChange={(e) => setLastname(e.target.value)}
                    autoComplete="false"
                    required
                  />
                </div>
              </div>
              <div className="input-group">
                <label htmlFor="phone">Phone</label>
                <input
                  value={phone}
                  onChange={(e) => setPhone(e.target.value)}
                  type="text"
                  placeholder="Phone"
                  autoComplete="false"
                  required
                />
              </div>
              <div className="input-group">
                <label htmlFor="address">Address</label>
                <textarea
                  value={address}
                  onChange={(e) => setAddress(e.target.value)}
                  required
                />
              </div>
              <div className="input__inline">
                <div className="input-group">
                  <label htmlFor="region">Region</label>
                  <select
                    value={state}
                    onSelect={(e) => setState(e.target.value)}>
                    <option value="siaya">Siaya</option>
                    <option value="kisumu">Kisumu</option>
                    <option value="nairobi">Nairobi</option>
                  </select>
                </div>
                <div className="input-group">
                  <label htmlFor="city">City</label>
                  <select
                    value={city}
                    onSelect={(e) => setCity(e.target.value)}>
                    <option value="siaya_town">Siaya Town</option>
                    <option value="kisumu_town">Kisumu Town</option>
                    <option value="nairobi_town">Nairobi Town</option>
                  </select>
                </div>
              </div>
              <div className="input-check">
                <input type="checkbox" /> Save this information for next time
              </div>
              <div className="checkout-nav">
                <Link to="/order-summary">
                  <i className="fa fa-chevron-left" />
                  Return to cart
                </Link>
                {cartItems?.length > 0 && (
                  <Button
                    loading={loading}
                    disabled={loading}
                    type="submit"
                    className="checkbox-btn">
                    Continue to payment
                  </Button>
                )}
              </div>
            </form>
          </div>
          <div className="checkout-summary">
            <h3 className="checkout-title">Order summary</h3>
            {cartItems?.length === 0 && (
              <>
                <div className="checkout-summary__empty">
                  <p>Your cart is empty</p>
                  <Link to="/all-products">Continue Shopping</Link>
                </div>
              </>
            )}
            {cartItems?.length > 0 && (
              <>
                <div
                  className={
                    cart?.order_items.length > 3
                      ? "checkout-orders scroll"
                      : "checkout-orders"
                  }>
                  {cartItems &&
                    cartItems.map((item) => (
                      <div className="checkout-order">
                        <div className="checkout-order__img">
                          <img
                            src={getImage(item.item_obj.image)}
                            alt={item?.item}
                          />
                          <span>{item?.quantity}</span>
                        </div>
                        <div className="checkout-order__detail">
                          <h3>{item?.item}</h3>
                          <p className="text-muted">
                            {item?.item_obj.weight} / {item?.item_obj.category}{" "}
                            / Peppermint
                          </p>
                        </div>
                        <h3>KSh. {item?.final_price}</h3>
                      </div>
                    ))}
                </div>
                <div className="underline" />
                <div className="checkout-subtotal">
                  <div>
                    <h3 className="text-muted">Subtotal</h3>
                    <h3 className="checkout-subtotal__price">
                      KSh. {cart?.total}
                    </h3>
                  </div>
                  <div>
                    <h3 className="text-muted">Shipping</h3>
                    <p className="text-muted">Calculated at next step</p>
                  </div>
                </div>
                <div className="underline" />
                <div className="checkout-total">
                  <h3>Total</h3>
                  <h2>KSh. {cart?.total}</h2>
                </div>
              </>
            )}
          </div>
        </div>
      </Container>
    </>
  );
};

const mapStateToProps = (state) => ({
  cart: state.cart.shoppingCart,
  loading: state.address.loading,
});

export default connect(mapStateToProps, { fetchCart, addAddress })(Checkout);
