import React, { useState, useEffect } from "react";
import { FiCheckCircle } from "react-icons/fi";
import { Button } from "semantic-ui-react";
import { connect } from "react-redux";
import { saveAddress } from "../redux/action/address";
import { authAxios } from "../utils";
import { addressURL } from "../constant";

const AddressForm = ({ saveAddress }) => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [phone, setPhone] = useState("");
  const [extra, setExtra] = useState("");
  const [address, setAddress] = useState("");
  const [state, setState] = useState("Nairobi");
  const [city, setCity] = useState("Nairobi");

  const [loading, setLoading] = useState(false);
  const [userAddress, setUserAddress] = useState(null);
  const [error, setError] = useState(null);

  const [saved, setSaved] = useState(true);
  const [show, setShow] = useState(false);

  useEffect(() => {
    fetchAddress();
  }, []);

  console.log(userAddress);

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = { firstName, lastName, phone, extra, address, state, city };
    setSaved(false);
    saveAddress(data);
    setShow(true);
  };

  const fetchAddress = () => {
    setLoading(true);
    authAxios
      .get(addressURL)
      .then((res) => setUserAddress(res.data))
      .catch((err) => {
        setError(err);
      });
  };

  const setActive = (address) => {
    console.log(address);
  };

  return (
    <div className="checkout_content-item">
      <div className="checkout_content-item__title">
        <h3>
          <FiCheckCircle />
          1. address details
        </h3>
        <div className="add-address">
          <i className="fa fa-plus" /> Add New Address
        </div>
      </div>
      <div className="underline" />
      <div className="address_list">
        {userAddress &&
          userAddress?.results.map((address) => (
            <div
              onClick={() => setActive(address)}
              className={
                address?.active ? "address_item active" : "address_item"
              }>
              <div className="address_item-title">
                <h4>
                  {address.first_name} {address.last_name}
                </h4>
                {address.default && <span>Default</span>}
              </div>
              <p className="text-muted address-place">
                {address.delivery_address}
              </p>
              <h5 className="address-town">{address.state_or_region}</h5>
              <p className="text-muted address-phone">
                +254 {address.phone_number}
              </p>
            </div>
          ))}
      </div>
    </div>
  );
};

export default connect(null, { saveAddress })(AddressForm);
