import React, { useEffect } from "react";
import { Container } from "semantic-ui-react";
import { fetchCart } from "../redux/action/cart";
import { Link } from "react-router-dom";
import img1 from "../assets/imgs/shampoo1.jpg";
import img2 from "../assets/imgs/shampoo2.jpg";
import AddressForm from "./AddressForm";
import PaymentMethod from "./PaymentMethod";
import { connect } from "react-redux";
import { getImage } from "../utils";

const Checkout = ({ fetchCart, cart }) => {
  useEffect(() => {
    fetchCart();
  }, []);

  let cartItems = cart?.order_items;
  console.log(cartItems);

  document.title = `Checkout ( ${cart?.order_items.length} items ) | Luxury Silk Online`;
  return (
    <>
      <div className="title">
        <p className="title-nav">
          <Link to="/">Home</Link>/
          <Link to="/order-summary">Order Summary</Link>/<span>Checkout</span>
        </p>
        <h2 className="title-name">Checkout</h2>
      </div>
      <Container className="checkout">
        <div className="checkout__wrapper">
          <div className="checkout-address">
            <h3 className="checkout-title">Delivery address</h3>
          </div>
          <div className="checkout-summary">
            <h3 className="checkout-title">Order summary</h3>
            <div className="checkout-orders">
              {cartItems &&
                cartItems.map((item) => (
                  <div className="checkout-order">
                    <div className="checkout-order__img">
                      <img
                        src={getImage(item.item_obj.image)}
                        alt={item?.item}
                      />
                      <span>{item?.quantity}</span>
                    </div>
                    <div>
                      <h3>{item?.item}</h3>
                      <p>
                        {item?.item_obj.weight}/{item?.item_obj.category}
                        /Peppermint
                      </p>
                    </div>
                    <h3>{item?.final_price}</h3>
                  </div>
                ))}
            </div>
          </div>
        </div>
      </Container>
    </>
  );
};

const mapStateToProps = (state) => ({
  cart: state.cart.shoppingCart,
});

export default connect(mapStateToProps, { fetchCart })(Checkout);
