import React, { useState, useEffect } from "react";
import { getImage } from "../utils";
import { IoMdTrash } from "react-icons/io";

const OrderItem = ({ item }) => {
  const [quantity, setQuantity] = useState(null);

  useEffect(() => {
    setQuantity(item.quantity);
  }, []);

  return (
    <>
      <div className="cart-item">
        <div className="cart-item__img">
          <IoMdTrash />
          <img src={getImage(item?.item_obj.image)} alt="" />
        </div>
        <div className="cart-item__info">
          <h3>{item.item}</h3>
          <p>
            {item?.item_obj.weight} / {item?.item_obj.hair_type} /{" "}
            {item?.item_obj.material} / Peppermint
          </p>
          <h4>KSh. {item?.item_obj.price}</h4>
          <div className="product-add">
            <div className="qty-selection">
              <a
                onClick={() => (quantity > 1 ? setQuantity(quantity - 1) : "")}
                field="quantity"
                className="down quantity-control-down">
                -
              </a>
              <span className="quantity">{quantity}</span>
              <a
                onClick={() => setQuantity(quantity + 1)}
                field="quantity"
                className="up quantity-control-up">
                +
              </a>
            </div>
          </div>
          <h4 className="cart-item__total">
            Total: KSh. {quantity * item.price}
          </h4>
        </div>
      </div>
      <div className="underline" />
    </>
  );
};

export default OrderItem;
