import React, { useState } from "react";
import { FiCheckCircle } from "react-icons/fi";
import { Button } from "semantic-ui-react";
import { connect } from "react-redux";
import { saveAddress } from "../redux/action/address";

const AddressForm = ({ saveAddress }) => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [phone, setPhone] = useState("");
  const [extra, setExtra] = useState("");
  const [address, setAddress] = useState("");
  const [state, setState] = useState("Nairobi");
  const [city, setCity] = useState("Nairobi");

  const [saved, setSaved] = useState(true);
  const [show, setShow] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = { firstName, lastName, phone, extra, address, state, city };
    setSaved(false);
    saveAddress(data);
    setShow(true);
  };

  return (
    <div className="checkout_content-item">
      <div className="checkout_content-item__title">
        <h3>
          <FiCheckCircle />
          1. address details
        </h3>
        <div className="saved">
          {saved && <h4 onClick={() => setShow(!show)}>change</h4>}
        </div>
      </div>
      <div className="underline" />
      {show && (
        <form onSubmit={(e) => handleSubmit(e)} className="address_form">
          <div className="input__inline">
            <div className="input__group">
              <label htmlFor="firstname">First Name *</label>
              <input
                type="text"
                value={firstName}
                required
                onChange={(e) => setFirstName(e.target.value)}
              />
            </div>
            <div className="input__group">
              <label htmlFor="lastname">Last Name *</label>
              <input
                type="text"
                value={lastName}
                required
                onChange={(e) => setLastName(e.target.value)}
              />
            </div>
          </div>
          <div className="input__group mobile">
            <label htmlFor="phone">Mobile phone number *</label>
            <div>
              <span className="phone_code">+254</span>
              <input
                type="text"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                required
                placeholder="70100000"
              />
            </div>
          </div>
          <div className="input__group address">
            <label htmlFor="address">Delivery Address*</label>
            <textarea
              rows="5"
              value={address}
              required
              onChange={(e) => setAddress(e.target.value)}
            />
          </div>
          <div className="input__group extra">
            <label htmlFor="extra">Additional Info</label>
            <textarea
              rows="5"
              value={extra}
              onChange={(e) => setExtra(e.target.value)}
            />
          </div>
          <div className="input__group state">
            <label htmlFor="state/region">State/Region *</label>
            <select
              required
              value={state}
              onChange={(e) => setState(e.target.value)}>
              <option value="nairobi">Nairobi</option>
              <option value="kisumu">Kisumu</option>
            </select>
          </div>
          <div className="input__group state">
            <label htmlFor="city">City *</label>
            <select
              required
              value={city}
              onChange={(e) => setCity(e.target.value)}>
              <option value="nairobi">Nairobi</option>
              <option value="kisumu">Kisumu Town</option>
            </select>
          </div>
          <p className="required">Required *</p>
          <Button type="submit">Save and continue</Button>
        </form>
      )}
      {!show && (
        <div className="address_closed">
          <h4>Maurice Oluoch</h4>
          <p className="text-muted">P.O Box 144 Siaya, Siaya Town</p>
          <p className="text-muted">+254795772333</p>
        </div>
      )}
    </div>
  );
};

export default connect(null, { saveAddress })(AddressForm);
